    void do_read_header() {
        boost::asio::async_read(socket_, boost::asio::buffer(&read_msg_.length, sizeof(read_msg_.length)),
            [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                if (!ec) {
                    do_read_body();
                }
            });
    }

    void do_read_body() {
        read_msg_.body.resize(read_msg_.length);
        boost::asio::async_read(socket_, boost::asio::buffer(read_msg_.body),
            [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                if (!ec) {
                    do_write_output();
                }
            });
    }

    void do_write_output() {
        boost::asio::async_write(output_, boost::asio::buffer(read_msg_.body),
            [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                if (!ec) {
                    do_read_header();
                }
            });
    }

    void do_read_input() {
        input_.async_read_some(boost::asio::buffer(input_buffer_),
            [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                if (!ec) {
                    std::string input_data = boost::asio::buffer_cast<const char*>(input_buffer_.data());
                    write_msg_.length = input_data.size();
                    write_msg_.body = input_data;
                    do_write_message();
                    do_read_input();
                }
            });
    }

    void do_write_message() {
        boost::asio::async_write(socket_, boost::asio::buffer(&write_msg_.length, sizeof(write_msg_.length)),
            [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                if (!ec) {
                    boost::asio::async_write(socket_, boost::asio::buffer(write_msg_.body),
                        [this](boost::system::error_code ec, std::size_t bytes_transferred) {
                            if (!ec) {
                                do_read_input();
                            }
                        });
                }
            });
    }

    void close() {
        socket_.close();
        input_.close();
        output_.close();
    }

private:
    boost::asio::ip::tcp::socket socket_;
    boost::asio::posix::stream_descriptor input_;
    boost::asio::posix::stream_descriptor output_;
    chat_message read_msg_;
    chat_message write_msg_;
    boost::asio::streambuf input_buffer_;
};

