#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <cstring>
#include <stdexcept>

#define MAXSOCK 10
#define TIMEOUT_SEC 5

class NetworkClientInfo {
public:
    int socket_fd;
    sockaddr_in client_addr;
    std::string ip_address;
    bool connected;

    NetworkClientInfo(int fd, sockaddr_in addr) : socket_fd(fd), client_addr(addr), connected(true) {
        char ip_str[INET_ADDRSTRLEN];
        inet_ntop(AF_INET, &(client_addr.sin_addr), ip_str, INET_ADDRSTRLEN);
        ip_address = ip_str;
    }
};

class RGBController {
public:
    virtual void Update() = 0;
    virtual ~RGBController() {}
};

class NetworkServer {
private:
    std::string host;
    unsigned short port_num;
    bool server_online;
    bool server_listening;
    std::vector<NetworkClientInfo*> ServerClients;
    std::mutex ServerClientsMutex;
    int server_sock[MAXSOCK];
    int socket_count;
    std::thread* ConnectionThread[MAXSOCK];

public:
    NetworkServer(std::vector<RGBController*>& control) : host("0.0.0.0"), port_num(8080), server_online(false), server_listening(false), socket_count(0) {}

    ~NetworkServer() {
        StopServer();
    }

    void StartServer() {
        if (server_online) return;

        server_online = true;
        server_listening = true;

        for (int i = 0; i < MAXSOCK; ++i) {
            server_sock[i] = socket(AF_INET, SOCK_STREAM, 0);
            if (server_sock[i] < 0) {
                throw std::runtime_error("Failed to create socket");
            }

            sockaddr_in server_addr;
            memset(&server_addr, 0, sizeof(server_addr));
            server_addr.sin_family = AF_INET;
            server_addr.sin_port = htons(port_num);
            inet_pton(AF_INET, host.c_str(), &server_addr.sin_addr);

            if (bind(server_sock[i], (struct sockaddr*)&server_addr, sizeof(server_addr)) < 0) {
                close(server_sock[i]);
                throw std::runtime_error("Failed to bind socket");
            }

            if (listen(server_sock[i], 10) < 0) {
                close(server_sock[i]);
                throw std::runtime_error("Failed to listen on socket");
            }

            socket_count++;
            ConnectionThread[i] = new std::thread(&NetworkServer::ConnectionThreadFunction, this, i);
        }
    }

    void StopServer() {
        if (!server_online) return;

        server_online = false;
        server_listening = false;

        for (int i = 0; i < MAXSOCK; ++i) {
            if (ConnectionThread[i]) {
                ConnectionThread[i]->join();
                delete ConnectionThread[i];
                ConnectionThread[i] = nullptr;
            }
            if (server_sock[i] >= 0) {
                close(server_sock[i]);
                server_sock[i] = -1;
            }
        }

        socket_count = 0;

        std::lock_guard<std::mutex> lock(ServerClientsMutex);
        for (auto client : ServerClients) {
            delete client;
        }
        ServerClients.clear();
    }

    void ConnectionThreadFunction(int socket_idx) {
        while (server_online) {
            int client_fd = accept_select(socket_idx);
            if (client_fd >= 0) {
                sockaddr_in client_addr;
                socklen_t addr_len = sizeof(client_addr);
                getpeername(client_fd, (struct sockaddr*)&client_addr, &addr_len);

                NetworkClientInfo* client_info = new NetworkClientInfo(client_fd, client_addr);
                {
                    std::lock_guard<std::mutex> lock(ServerClientsMutex);
                    ServerClients.push_back(client_info);
                }

                // Start a thread to handle communication with the client
                std::thread* listen_thread = new std::thread(&NetworkServer::ListenThreadFunction, this, client_info);
                listen_thread->detach();
            }
        }
    }

    int accept_select(int sockfd) {
        fd_set read_fds;
        FD_ZERO(&read_fds);
        FD_SET(sockfd, &read_fds);

        timeval timeout;
        timeout.tv_sec = TIMEOUT_SEC;
        timeout.tv_usec = 0;

        int ret = select(sockfd + 1, &read_fds, nullptr, nullptr, &timeout);
        if (ret < 0) {
            return -1;
        } else if (ret == 0) {
            return -1;
        }

        if (FD_ISSET(sockfd, &read_fds)) {
            int client_fd = accept(sockfd, nullptr, nullptr);
            if (client_fd < 0) {
                return -1;
            }
            return client_fd;
        }

        return -1;
    }

    unsigned short GetPort() {
        return port_num;
    }

    void ClientInfoChanged() {
        // Callback for client list changes
    }

    void ServerListeningChanged() {
        // Callback for server listening state changes
    }

    void ListenThreadFunction(NetworkClientInfo* client_info) {
        char buffer[1024];
        while (client_info->connected) {
            int bytes_received = recv(client_info->socket_fd, buffer, sizeof(buffer), 0);
            if (bytes_received <= 0) {
                client_info->connected = false;
                break;
            }

            // Process received data
            std::cout << "Received from " << client_info->ip_address << ": " << buffer << std::endl;
        }

        {
            std::lock_guard<std::mutex> lock(ServerClientsMutex);
            for (auto it = ServerClients.begin(); it != ServerClients.end(); ++it) {
                if (*it == client_info) {
                    delete *it;
                    ServerClients.erase(it);
                    break;
                }
            }
        }

        close(client_info->socket_fd);
    }
};

