    }

    static bool isInit() {
        return instance_ != nullptr;
    }

    static bool isInitInternal() {
        return instance_ != nullptr;
    }

    Stats(const std::string& stats_socket_path) : stats_socket_path_(stats_socket_path), sockfd_(-1), statsThreadRunning_(false), thread_count_(0) {}

    ~Stats() {
        if (statsThreadRunning_) {
            statsThreadRunning_ = false;
            stats_thread_.join();
        }
        if (sockfd_ != -1) {
            close(sockfd_);
        }
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] += val;
        return stats_[key];
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return stats_[key];
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

    bool startSocket() {
        if (sockfd_ != -1) {
            return false;
        }
        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ == -1) {
            return false;
        }
        serv_addr_.sun_family = AF_UNIX;
        strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path) - 1);
        unlink(stats_socket_path_.c_str());
        if (bind(sockfd_, (struct sockaddr*)&serv_addr_, sizeof(serv_addr_)) == -1) {
            close(sockfd_);
            sockfd_ = -1;
            return false;
        }
        listen(sockfd_, 5);
        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);
        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            struct sockaddr_un client_addr;
            socklen_t client_len = sizeof(client_addr);
            int client_fd = accept(sockfd_, (struct sockaddr*)&client_addr, &client_len);
            if (client_fd != -1) {
                std::lock_guard<std::mutex> lock(thread_mutex_);
                ++thread_count_;
                std::thread([this, client_fd]() {
                    processMsg(client_fd);
                    std::lock_guard<std::mutex> lock(thread_mutex_);
                    --thread_count_;
                    thread_exited_.notify_one();
                }).detach();
            }
        }
    }

    void processMsg(int sockfd) {
        char buffer[1024];
        ssize_t bytes_read = read(sockfd, buffer, sizeof(buffer) - 1);
        if (bytes_read > 0) {
            buffer[bytes_read] = '\0';
            std::string request(buffer);
            if (request.substr(0, 4) == "GET ") {
                std::string key = request.substr(4);
                std::lock_guard<std::mutex> lock(stats_mutex_);
                auto it = stats_.find(key);
                if (it != stats_.end()) {
                    std::string response = std::to_string(it->second);
                    write(sockfd, response.c_str(), response.size());
                } else {
                    write(sockfd, "0", 1);
                }
            } else if (request.substr(0, 6) == "RESET ") {
                reset();
                write(sockfd, "OK", 2);
            }
        }
        close(sockfd);
    }

private:
    std::string stats_socket_path_;
    int sockfd_;
    struct sockaddr_un serv_addr_;
    std::thread stats_thread_;
    std::atomic<bool> statsThreadRunning_;
    std::mutex stats_mutex_;
    std::mutex thread_mutex_;
    std::condition_variable thread_exited_;
    int thread_count_;
    std::unordered_map<std::string, int> stats_;
    static std::unique_ptr<Stats> instance_;
};

