typedef websocketpp::server<websocketpp::config::asio_no_tls> server;

class broadcast_server {
private:
    server m_server;
    std::set<server::connection_hdl> m_connections;
    std::queue<std::function<void()>> m_actions;
    std::mutex m_action_lock;
    std::mutex m_connection_lock;
    std::condition_variable m_action_cond;

    void on_open(server::connection_hdl hdl) {
        std::lock_guard<std::mutex> lock(m_connection_lock);
        m_connections.insert(hdl);
        std::lock_guard<std::mutex> action_lock(m_action_lock);
        m_actions.push([this, hdl]() {
            std::cout << "New connection opened" << std::endl;
        });
        m_action_cond.notify_one();
    }

    void on_close(server::connection_hdl hdl) {
        std::lock_guard<std::mutex> lock(m_connection_lock);
        m_connections.erase(hdl);
        std::lock_guard<std::mutex> action_lock(m_action_lock);
        m_actions.push([this, hdl]() {
            std::cout << "Connection closed" << std::endl;
        });
        m_action_cond.notify_one();
    }

    void on_message(server::connection_hdl hdl, server::message_ptr msg) {
        std::lock_guard<std::mutex> lock(m_action_lock);
        m_actions.push([this, hdl, msg]() {
            std::string payload = msg->get_payload();
            std::cout << "Received message: " << payload << std::endl;
            broadcast(payload);
        });
        m_action_cond.notify_one();
    }

    void broadcast(const std::string& message) {
        std::lock_guard<std::mutex> lock(m_connection_lock);
        for (auto it = m_connections.begin(); it != m_connections.end(); ++it) {
            m_server.send(*it, message, websocketpp::frame::opcode::text);
        }
    }

    void process_messages() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_action_lock);
            m_action_cond.wait(lock, [this] { return !m_actions.empty(); });

            while (!m_actions.empty()) {
                auto action = m_actions.front();
                action();
                m_actions.pop();
            }
        }
    }

public:
    broadcast_server() {
        m_server.set_access_channels(websocketpp::log::alevel::none);
        m_server.init_asio();
        m_server.set_open_handler(std::bind(&broadcast_server::on_open, this, ::_1));
        m_server.set_close_handler(std::bind(&broadcast_server::on_close, this, ::_1));
        m_server.set_message_handler(std::bind(&broadcast_server::on_message, this, ::_1, ::_2));
    }

    void run(uint16_t port) {
        m_server.listen(port);
        m_server.start_accept();

