class HttpWebResponse {
public:
    std::string content;
    int status_code;
};

class HttpWebRequest {
private:
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::iostream* is_;

    static size_t callback(void* buffer, size_t size, size_t nmemb, void* data) {
        size_t realsize = size * nmemb;
        std::string* response = static_cast<std::string*>(data);
        response->append(static_cast<char*>(buffer), realsize);
        return realsize;
    }

    HttpWebResponse* GetFileResponse(const char* uri) {
        HttpWebResponse* response = new HttpWebResponse();
        std::ifstream file(uri + 7, std::ios::binary | std::ios::ate);
        if (file.is_open()) {
            std::streampos size = file.tellg();
            file.seekg(0, std::ios::beg);
            response->content.resize(size);
            file.read(&response->content[0], size);
            response->status_code = 200;
        } else {
            response->status_code = 404;
        }
        file.close();
        return response;
    }

    HttpWebResponse* GetNetResponse(const char* uri) {
        HttpWebResponse* response = new HttpWebResponse();
        CURL* curl = curl_easy_init();
        if (curl) {
            curl_easy_setopt(curl, CURLOPT_URL, uri);
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, &response->content);
            curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
            CURLcode res = curl_easy_perform(curl);
            if (res == CURLE_OK) {
                curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &response->status_code);
            } else {
                response->status_code = 500;
            }
            curl_easy_cleanup(curl);
        } else {
            response->status_code = 500;
        }
        return response;
    }

public:
    HttpWebRequest(std::string& uri) : uri_(uri), method_("GET"), is_(nullptr) {}

    HttpWebResponse* GetResponse() {
        if (uri_.substr(0, 7) == "file://") {
            return GetFileResponse(uri_.c_str());
        } else {
            return GetNetResponse(uri_.c_str());
        }
    }
};

