class Task {
public:
    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}

    void run() {
        CURL* curl = curl_easy_init();
        if (!curl) return;

        FILE* fp = fopen(m_output.c_str(), "wb");
        if (!fp) {
            curl_easy_cleanup(curl);
            return;
        }

        curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            std::cerr << "Download failed: " << m_url << std::endl;
        }

        fclose(fp);
        curl_easy_cleanup(curl);
    }

    static size_t writeData(void* buffer, size_t size, size_t nmemb, void* userp) {
        FILE* fp = static_cast<FILE*>(userp);
        return fwrite(buffer, size, nmemb, fp);
    }

private:
    std::string m_url;
    std::string m_output;
};

class DownloadPool {
public:
    DownloadPool() : m_noMoreTask(false) {
        curl_global_init(CURL_GLOBAL_DEFAULT);
        for (int i = 0; i < std::thread::hardware_concurrency(); ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_urls.insert(url);
            m_queue.push(Task(url, output));
            m_cv.notify_one();
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
        m_cv.notify_all();
    }

    void finishAndWaitForExit() {
        std::unique_lock<std::mutex> lock(m_mtx);
        m_cv.wait(lock, [this] { return m_queue.empty() && m_noMoreTask; });
        for (auto& thread : m_threads) {
            thread.join();
        }
    }

    void run() {
        while (true) {
            Task task;
            {
                std::unique_lock<std::mutex> lock(m_mtx);
                m_cv.wait(lock, [this] { return !m_queue.empty() || m_noMoreTask; });
                if (m_noMoreTask && m_queue.empty()) break;
                task = m_queue.front();
                m_queue.pop();
            }
            task.run();
        }
    }

private:
    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    std::condition_variable m_cv;
    bool m_noMoreTask;
