    void handle_read_header(const boost::system::error_code& error, std::size_t bytes_transferred) {
        if (!error && bytes_transferred == sizeof(read_msg_.length_)) {
            do_read_body();
        }
    }

    void do_read_body() {
        boost::asio::async_read(socket_, boost::asio::buffer(read_msg_.data_, read_msg_.length_),
            boost::bind(&posix_chat_client::handle_read_body, shared_from_this(), _1, _2));
    }

    void handle_read_body(const boost::system::error_code& error, std::size_t bytes_transferred) {
        if (!error && bytes_transferred == read_msg_.length_) {
            do_write_output();
        }
    }

    void do_write_output() {
        boost::asio::async_write(output_.descriptor_, boost::asio::buffer(read_msg_.data_, read_msg_.length_),
            boost::bind(&posix_chat_client::handle_write_output, shared_from_this(), _1, _2));
    }

    void handle_write_output(const boost::system::error_code& error, std::size_t bytes_transferred) {
        if (!error) {
            do_read_header();
        }
    }

    void do_read_input() {
        input_.async_read_some(boost::asio::buffer(input_buffer_),
            boost::bind(&posix_chat_client::handle_read_input, shared_from_this(), _1, _2));
    }

    void handle_read_input(const boost::system::error_code& error, std::size_t bytes_transferred) {
        if (!error && bytes_transferred > 0) {
            write_msg_.length_ = bytes_transferred;
            do_write_message();
        }
    }

    void do_write_message() {
        boost::asio::async_write(socket_, boost::asio::buffer(write_msg_.data_, write_msg_.length_),
            boost::bind(&posix_chat_client::handle_write_message, shared_from_this(), _1, _2));
    }

    void handle_write_message(const boost::system::error_code& error, std::size_t bytes_transferred) {
        if (!error) {
            do_read_input();
        }
    }

    void close() {
        socket_.close();
        input_.descriptor_.close();
        output_.descriptor_.close();
    }

private:
    boost::asio::ip::tcp::socket socket_;
    posix::stream_descriptor input_;
    posix::stream_descriptor output_;
    chat_message read_msg_;
    chat_message write_msg_;
    boost::asio::streambuf input_buffer_;
};

