class Logging {
public:
    virtual void log(const std::string& message) = 0;
};

class Device {
public:
    std::string host;
    int port;
    bool use_ssl;
};

class WebOsClient {
public:
    class Impl {
    private:
        net::io_context& ioc_;
        ssl::context& ctx_;
        Device& device_settings_;
        std::shared_ptr<Logging> log_;
        tcp::resolver resolver_;
        std::optional<websocket::stream<beast::ssl_stream<beast::tcp_stream>>> ws_;
        std::optional<websocket::stream<beast::tcp_stream>> ws_tcp_;
        beast::flat_buffer buffer_;
        std::string host_;
        int socket_status_;

        void run() {
            host_ = device_settings_.host + ":" + std::to_string(device_settings_.port);
            resolver_.async_resolve(host_, std::to_string(device_settings_.port),
                [this](beast::error_code ec, tcp::resolver::results_type results) {
                    onResolve(ec, results);
                });
        }

        void onResolve(beast::error_code ec, tcp::resolver::results_type results) {
            if (ec) {
                onError(ec, "resolve failed");
                return;
            }

            if (device_settings_.use_ssl) {
                ws_ = websocket::stream<beast::ssl_stream<beast::tcp_stream>>(ioc_, ctx_);
                ws_->set_option(websocket::stream_base::timeout::suggested(25));
                ws_->set_option(websocket::stream_base::decorator(
                    [](websocket::request_type& req) {
                        req.set(http::field::user_agent, "Boost.Beast");
                    }));
                net::connect(ws_->next_layer(), results);
                ws_->handshake("localhost", "/");
                ws_->async_handshake(
                    [this](beast::error_code ec) {
                        onSSLhandshake(ec);
                    });
            } else {
                ws_tcp_ = websocket::stream<beast::tcp_stream>(ioc_);
                ws_tcp_->set_option(websocket::stream_base::timeout::suggested(25));
                ws_tcp_->set_option(websocket::stream_base::decorator(
                    [](websocket::request_type& req) {
                        req.set(http::field::user_agent, "Boost.Beast");
                    }));
                net::connect(ws_tcp_->next_layer(), results);
                ws_tcp_->async_handshake(
                    [this](beast::error_code ec) {
                        onWinsockHandshake(ec);
                    });
            }
        }

        void onConnect(beast::error_code ec, tcp::resolver::results_type::endpoint_type endpoint) {
            if (ec) {
                onError(ec, "connect failed");
                return;
            }
        }

        void onSSLhandshake(beast::error_code ec) {
            if (ec) {
                onError(ec, "SSL handshake failed");
                return;
            }
            read();
        }

        void onWinsockHandshake(beast::error_code ec) {
            if (ec) {
                onError(ec, "WebSocket handshake failed");
                return;
            }
            read();
        }

        void send(std::string data, std::string type) {
            if (device_settings_.use_ssl && ws_) {
                ws_->text(type == "text");
                ws_->write(net::buffer(data), [this](beast::error_code ec, std::size_t bytes_transferred) {
                    onWrite(ec, bytes_transferred);
                });
            } else if (!device_settings_.use_ssl && ws_tcp_) {
                ws_tcp_->text(type == "text");
                ws_tcp_->write(net::buffer(data), [this](beast::error_code ec, std::size_t bytes_transferred) {
                    onWrite(ec, bytes_transferred);
                });
            }
        }

        void onWrite(beast::error_code ec, std::size_t bytes_transferred) {
            if (ec) {
                onError(ec, "write failed");
            }
        }

        void read() {
            buffer_.clear();
            if (device_settings_.use_ssl && ws_) {
                ws_->async_read(buffer_, [this](beast::error_code ec, std::size_t bytes_transferred) {
                    onRead(ec, bytes_transferred);
                });
            } else if (!device_settings_.use_ssl && ws_tcp_) {
                ws_tcp_->async_read(buffer_, [this](beast::error_code ec, std::size_t bytes_transferred) {
                    onRead(ec, bytes_transferred);
                });
            }
        }

        void onRead(beast::error_code ec, std::size_t bytes_transferred) {
            if (ec) {
                onError(ec, "read failed");
                return;
            }
            std::string message = beast::buffers_to_string(buffer_.data());
            // Process message
            read();
        }

        void onError(beast::error_code& ec, std::string message) {
            log_->log("Error: " + message + " - " + ec.message());
            close();
        }

        void enqueueWork(Work& work) {
            ioc_.post(work);
        }

        void close() {
            if (device_settings_.use_ssl && ws_) {
                ws_->close(websocket::close_code::normal);
            } else if (!device_settings_.use_ssl && ws_tcp_) {
                ws_tcp_->close(websocket::close_code::normal);
            }
        }

    public:
        Impl(net::io_context& ioc, ssl::context& ctx, Device& device, Logging& log)
            : ioc_(ioc), ctx_(ctx), device_settings_(device), log_(std::make_shared<Logging>(log)), resolver_(ioc_), socket_status_(0) {
            run();
        }
    };
};
