        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr);

        if (connect(sock, (struct sockaddr*)&serverAddr, sizeof(serverAddr)) == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to connect to server");
        }

        if (send(sock, msg.c_str(), msg.size(), 0) == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to send HTTP request");
        }

        char buffer[4096];
        ssize_t bytesReceived;
        std::string response;

        while ((bytesReceived = recv(sock, buffer, sizeof(buffer), 0)) > 0) {
            response.append(buffer, bytesReceived);
        }

        if (bytesReceived == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to receive HTTP response");
        }

        return response;
    }

    static std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to create UDP socket");
        }
        SocketCloser closer(sock);

        struct sockaddr_in multicastAddr;
        multicastAddr.sin_family = AF_INET;
        multicastAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &multicastAddr.sin_addr);

        if (sendto(sock, msg.c_str(), msg.size(), 0, (struct sockaddr*)&multicastAddr, sizeof(multicastAddr)) == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to send multicast message");
        }

        std::vector<std::string> responses;
        std::mutex mtx;
        std::condition_variable cv;
        bool done = false;

        std::thread receiver([=, &responses, &mtx, &cv, &done]() {
            char buffer[4096];
            struct sockaddr_in fromAddr;
            socklen_t fromLen = sizeof(fromAddr);

            auto end = std::chrono::steady_clock::now() + timeout;
            while (std::chrono::steady_clock::now() < end) {
                ssize_t bytesReceived = recvfrom(sock, buffer, sizeof(buffer), 0, (struct sockaddr*)&fromAddr, &fromLen);
                if (bytesReceived > 0) {
                    std::lock_guard<std::mutex> lock(mtx);
                    responses.push_back(std::string(buffer, bytesReceived));
                }
                std::this_thread::sleep_for(std::chrono::milliseconds(100));
            }

            {
                std::lock_guard<std::mutex> lock(mtx);
                done = true;
            }
            cv.notify_all();
        });

        cv.wait_for(std::unique_lock<std::mutex>(mtx), timeout);

        receiver.join();

        return responses;
    }
};

