#include <stdexcept>
#include <chrono>
#include <cstring>
#include <fcntl.h>

class LinHttpHandler {
public:
    class SocketCloser {
    private:
        int sockFd;
    public:
        SocketCloser(int fd) : sockFd(fd) {}
        ~SocketCloser() {
            if (sockFd >= 0) {
                close(sockFd);
            }
        }
    };

    static std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to create TCP socket");
        }
        SocketCloser closer(sock);

        struct sockaddr_in serverAddr;
        memset(&serverAddr, 0, sizeof(serverAddr));
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr);

        if (connect(sock, (struct sockaddr*)&serverAddr, sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to connect to server");
        }

        if (send(sock, msg.c_str(), msg.size(), 0) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to send HTTP message");
        }

        char buffer[4096];
        ssize_t bytesReceived;
        std::string response;

        while ((bytesReceived = recv(sock, buffer, sizeof(buffer), 0)) > 0) {
            response.append(buffer, bytesReceived);
        }

        if (bytesReceived < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to receive HTTP response");
        }

        return response;
    }

    static std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to create UDP socket");
        }
        SocketCloser closer(sock);

        int reuse = 1;
        if (setsockopt(sock, SOL_SOCKET, SO_REUSEADDR, &reuse, sizeof(reuse)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to set SO_REUSEADDR");
        }

        struct sockaddr_in localAddr;
        memset(&localAddr, 0, sizeof(localAddr));
        localAddr.sin_family = AF_INET;
        localAddr.sin_port = htons(0);
        localAddr.sin_addr.s_addr = INADDR_ANY;

        if (bind(sock, (struct sockaddr*)&localAddr, sizeof(localAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to bind UDP socket");
        }

        struct ip_mreq mreq;
        memset(&mreq, 0, sizeof(mreq));
        mreq.imr_multiaddr.s_addr = inet_addr(adr.c_str());
        mreq.imr_interface.s_addr = INADDR_ANY;

        if (setsockopt(sock, IPPROTO_IP, IP_ADD_MEMBERSHIP, &mreq, sizeof(mreq)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to join multicast group");
        }

        struct sockaddr_in serverAddr;
        memset(&serverAddr, 0, sizeof(serverAddr));
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr);

        if (sendto(sock, msg.c_str(), msg.size(), 0, (struct sockaddr*)&serverAddr, sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to send multicast message");
        }

        std::vector<std::string> responses;
        fd_set readfds;
        struct timeval tv;

        auto end_time = std::chrono::steady_clock::now() + timeout;
        while (std::chrono::steady_clock::now() < end_time) {
            FD_ZERO(&readfds);
            FD_SET(sock, &readfds);

            tv.tv_sec = 0;
            tv.tv_usec = 100000; // 100 ms

            int activity = select(sock + 1, &readfds, nullptr, nullptr, &tv);
            if (activity < 0) {
                throw std::system_error(errno, std::generic_category(), "Select failed");
            }

            if (activity > 0 && FD_ISSET(sock, &readfds)) {
                char buffer[4096];
                struct sockaddr_in fromAddr;
                socklen_t fromLen = sizeof(fromAddr);
                ssize_t bytesReceived = recvfrom(sock, buffer, sizeof(buffer), 0, (struct sockaddr*)&fromAddr, &fromLen);
                if (bytesReceived > 0) {
                    responses.emplace_back(buffer, bytesReceived);
                }
            }
        }

        return responses;
    }
};

