#include <fcntl.h>
#include <folly/Conv.h>
#include <folly/ScopeGuard.h>
#include <glog/logging.h>
#include <poll.h>
#include <sys/socket.h>
#include <wdt/Reporting.h>
#include <wdt/util/ClientSocket.h>

namespace facebook {
namespace wdt {

using std::string;

class ClientSocket {
public:
  ClientSocket(ThreadCtx &threadCtx, const string &dest,
               const int port,
               const EncryptionParams &encryptionParams,
               int64_t ivChangeInterval)
      : dest_(dest), threadCtx_(threadCtx) {
    memset(&sa_, 0, sizeof(sa_));
    socket_ = std::make_unique<WdtSocket>(threadCtx, port, encryptionParams,
                                          ivChangeInterval, nullptr);
    if (threadCtx_.getOptions().ipv6) {
      sa_.ai_family = AF_INET6;
    }
    if (threadCtx_.getOptions().ipv4) {
      sa_.ai_family = AF_INET;
    }
    sa_.ai_socktype = SOCK_STREAM;
  }

  ErrorCode connect() {
    auto fd = socket_->getFd();
    auto port = socket_->getPort();
    WDT_CHECK(fd < 0) << "Previous connection not closed " << fd << " " << port;

    struct addrinfo *infoList = nullptr;
    auto guard = folly::makeGuard([&] {
      if (infoList) {
        freeaddrinfo(infoList);
      }
    });

    string portStr = folly::to<string>(port);
    int res = getaddrinfo(dest_.c_str(), portStr.c_str(), &sa_, &infoList);
    if (res) {
      WLOG(ERROR) << "Failed getaddrinfo " << dest_ << " , " << port << " : "
                  << res << " : " << gai_strerror(res);
      return CONN_ERROR;
    }

    int count = 0;
    for (struct addrinfo *info = infoList; info != nullptr;
         info = info->ai_next) {
      ++count;
      std::string host, port_2;
      WdtSocket::getNameInfo(info->ai_addr, info->ai_addrlen, host, port_2);
      WVLOG(2) << "will connect to " << host << " " << port_2;

      fd = socket(info->ai_family, info->ai_socktype, info->ai_protocol);
      if (fd == -1) {
        WPLOG(WARNING) << "Error making socket for port_2 " << port_2;
        continue;
      }

      socket_->setFd(fd);
      setSendBufferSize();

      int sockArg = fcntl(fd, F_GETFL, nullptr);
      sockArg |= O_NONBLOCK;
      res = fcntl(fd, F_SETFL, sockArg);
      if (res < 0) {
        WPLOG(ERROR) << "Failed to make the socket non-blocking " << port_2
                     << " sock " << sockArg << " res " << res;
        closeConnection();
        continue;
      }

      if (::connect(fd, info->ai_addr, info->ai_addrlen) != 0) {
        if (errno != EINPROGRESS) {
          WPLOG(INFO) << "Error connecting on " << host << " " << port_2;
          closeConnection();
          continue;
        }

        auto startTime = Clock::now();
        int connectTimeout = threadCtx_.getOptions().connect_timeout_millis;

        while (true) {
          if (threadCtx_.getAbortChecker()->shouldAbort()) {
            WLOG(ERROR) << "Transfer aborted during connect " << port_2 << " "
                        << fd;
            closeConnection();
            return ABORT;
          }

          int timeElapsed = durationMillis(Clock::now() - startTime);
          if (timeElapsed >= connectTimeout) {
            WVLOG(1) << "connect() timed out" << host << " " << port_2;
            closeConnection();
            return CONN_ERROR_RETRYABLE;
          }

          int pollTimeout =
              std::min(connectTimeout - timeElapsed,
                       threadCtx_.getOptions().abort_check_interval_millis);
          struct pollfd pollFds[] = {{fd, POLLOUT, 0}};

          if ((res = poll(pollFds, 1, pollTimeout)) <= 0) {
            if (errno == EINTR) {
              WVLOG(1) << "poll() call interrupted. retrying... " << port_2;
              continue;
            }
            if (res == 0) {
              WVLOG(1) << "poll() timed out " << host << " " << port_2;
              continue;
            }
            WPLOG(ERROR) << "poll() failed " << host << " " << port_2 << " "
                         << fd;
            closeConnection();
            return CONN_ERROR;
          }
          break;
        }

        int connectResult;
        socklen_t len = sizeof(connectResult);
        if (getsockopt(fd, SOL_SOCKET, SO_ERROR, &connectResult, &len) < 0) {
          WPLOG(WARNING) << "getsockopt() failed";
          closeConnection();
          continue;
        }
        if (connectResult != 0) {
          WLOG(WARNING) << "connect did not succeed on " << host << " " << port_2
                        << " : " << strerrorStr(connectResult);
          closeConnection();
          continue;
        }
      }

      sockArg = fcntl(fd, F_GETFL, nullptr);
      sockArg &= (~O_NONBLOCK);
      res = fcntl(fd, F_SETFL, sockArg);
      if (res == -1) {
        WPLOG(ERROR) << "Could not make the socket blocking " << port_2;
        closeConnection();
        continue;
      }

      WVLOG(1) << "Successful connect on " << fd;
      peerIp_ = host;
      sa_ = *info;
      break;
    }

    if (socket_->getFd() < 0) {
      if (count > 1) {
        WLOG(INFO) << "Unable to connect to either of the " << count << " addrs";
      }
      return CONN_ERROR_RETRYABLE;
    }

    socket_->setSocketTimeouts();
    socket_->setDscp(threadCtx_.getOptions().dscp);
    return OK;
  }

  const std::string &getPeerIp() const {
    return peerIp_;
  }

  void setSendBufferSize() {
    int bufSize = threadCtx_.getOptions().send_buffer_size;
    auto fd = socket_->getFd();
    auto port = socket_->getPort();
    if (bufSize <= 0) {
      return;
    }
    int status =
        ::setsockopt(fd, SOL_SOCKET, SO_SNDBUF, &bufSize, sizeof(bufSize));
    if (status != 0) {
      WPLOG(ERROR) << "Failed to set send buffer " << port << " size " << bufSize
                   << " fd " << fd;
      return;
    }
    WVLOG(1) << "Send buffer size set to " << bufSize << " port " << port;
  }

private:
  std::string dest_;
  ThreadCtx &threadCtx_;
  struct addrinfo sa_;
  std::unique_ptr<WdtSocket> socket_;
  std::string peerIp_;

  void closeConnection() {
    socket_->close();
  }
};

}  // namespace wdt
}  // namespace facebook

