    ~SocketHolder() { if (fd_ != -1) close(fd_); }
    int get() const { return fd_; }
    void reset(int fd = -1) { if (fd_ != -1) close(fd_); fd_ = fd; }
private:
    int fd_;
};

class TCPClient {
public:
    TCPClient(const std::string& address, uint16_t port) : address_(address), port_(port), connected_(false) {}

    bool Connect() {
        main_socket_.reset(socket(AF_INET, SOCK_STREAM, 0));
        if (main_socket_.get() == -1) return false;

        struct sockaddr_in server_addr;
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port_);
        inet_pton(AF_INET, address_.c_str(), &server_addr.sin_addr);

        if (ConnectWithTimeout(main_socket_.get(), &server_addr, sizeof(server_addr), 5) == false) {
            return false;
        }

        connected_ = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected_) return false;

        fd_set read_fds;
        FD_ZERO(&read_fds);
        FD_SET(main_socket_.get(), &read_fds);

        timeval tv;
        tv.tv_sec = timeout.count() / 1000000000;
        tv.tv_usec = (timeout.count() % 1000000000) / 1000;

        int select_result = select(main_socket_.get() + 1, &read_fds, nullptr, nullptr, &tv);
        if (select_result <= 0) return false;

        ssize_t bytes_read = recv(main_socket_.get(), buffer, size, 0);
        return bytes_read == static_cast<ssize_t>(size);
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected_) return false;

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(main_socket_.get(), &write_fds);

        timeval tv;
        tv.tv_sec = timeout.count() / 1000000000;
        tv.tv_usec = (timeout.count() % 1000000000) / 1000;

        int select_result = select(main_socket_.get() + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result <= 0) return false;

        ssize_t bytes_written = send(main_socket_.get(), buffer, size, 0);
        return bytes_written == static_cast<ssize_t>(size);
    }

    void Disconnect() {
        if (connected_) {
            shutdown(main_socket_.get(), SHUT_RDWR);
            connected_ = false;
        }
    }

    bool SetNonBlock(int sockFD, bool nonBlocking) {
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;

        if (nonBlocking) {
            flags |= O_NONBLOCK;
        } else {
            flags &= ~O_NONBLOCK;
        }

        return fcntl(sockFD, F_SETFL, flags) == 0;
    }

    bool ConnectWithTimeout(int sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) return false;

        int result = connect(sockFD, static_cast<struct sockaddr*>(server_addr), static_cast<socklen_t>(server_addr_len));
        if (result == 0) {
            SetNonBlock(sockFD, false);
            return true;
        }

        if (errno != EINPROGRESS) return false;

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result <= 0) {
            SetNonBlock(sockFD, false);
            return false;
        }

        int so_error;
        socklen_t error_len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &error_len) < 0) {
            SetNonBlock(sockFD, false);
            return false;
        }

        if (so_error != 0) {
            SetNonBlock(sockFD, false);
            return false;
        }

        SetNonBlock(sockFD, false);
        return true;
    }

private:
    std::string address_;
    uint16_t port_;
    SocketHolder main_socket_;
    bool connected_;
};

