        CURLcode resCode;
        long httpCode = 0;

        curl = curl_easy_init();
        if (curl) {
            MemoryStruct chunk;
            chunk.memory = (char*)malloc(1);
            chunk.size = 0;

            curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
