typedef struct stEndPoint {
    char* ip;
    unsigned short int port;
} stEndPoint;

void* readwrite_routine(void* arg) {
    stEndPoint* endpoint = (stEndPoint*)arg;
    int sock = -1;
    struct sockaddr_in server_addr;
    char buffer[1024];
    const char* message = "Hello from client";

    while (1) {
        if (sock != -1) {
            close(sock);
            sock = -1;
        }

        sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock == -1) {
            perror("Socket creation failed");
            sleep(1);
            continue;
        }

        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(endpoint->port);

        if (endpoint->ip == NULL || strcmp(endpoint->ip, "0.0.0.0") == 0 || strcmp(endpoint->ip, "localhost") == 0) {
            server_addr.sin_addr.s_addr = INADDR_LOOPBACK;
        } else {
            if (inet_pton(AF_INET, endpoint->ip, &server_addr.sin_addr) <= 0) {
                perror("Invalid IP address");
                close(sock);
                sock = -1;
                sleep(1);
                continue;
            }
        }

        if (connect(sock, (struct sockaddr*)&server_addr, sizeof(server_addr)) < 0) {
            perror("Connection failed");
            close(sock);
            sock = -1;
            sleep(1);
            continue;
        }

        printf("Connected to server\n");

        while (1) {
            ssize_t bytes_sent = send(sock, message, strlen(message), 0);
            if (bytes_sent < 0) {
                perror("Send failed");
                break;
