        while (server_online) {
            int client_socket = accept_select(socket_idx);
            if (client_socket >= 0) {
                NetworkClientInfo *client_info = new NetworkClientInfo();
                client_info->socket_fd = client_socket;

                sockaddr_in client_addr;
                socklen_t addr_len = sizeof(client_addr);
                getpeername(client_socket, (struct sockaddr*)&client_addr, &addr_len);
                client_info->ip_address = inet_ntoa(client_addr.sin_addr);
                client_info->port = std::to_string(ntohs(client_addr.sin_port));

                {
                    std::lock_guard<std::mutex> lock(ServerClientsMutex);
                    ServerClients.push_back(client_info);
                }

                ClientInfoChanged();
            }
        }
    }

    int accept_select(int sockfd) {
        fd_set readfds;
        FD_ZERO(&readfds);
        FD_SET(sockfd, &readfds);

        timeval timeout;
        timeout.tv_sec = TIMEOUT_SEC;
        timeout.tv_usec = 0;

        int select_result = select(sockfd + 1, &readfds, nullptr, nullptr, &timeout);
        if (select_result > 0 && FD_ISSET(sockfd, &readfds)) {
            int client_socket = accept(sockfd, nullptr, nullptr);
            if (client_socket >= 0) {
                return client_socket;
            }
        }

        return -1;
    }

