#include <iostream>
#include <cstring>
#include <unistd.h>
#include <arpa/inet.h>
#include <sys/poll.h>
#include <thread>
#include <atomic>

class DFNetWork;

class UDPRecv {
public:
    UDPRecv(DFNetWork* poDFNetWork);
    ~UDPRecv();

    void Stop();
    int Init(int iPort);
    void run();

private:
    DFNetWork* m_poDFNetWork;
    int m_iSockFD;
    std::atomic<bool> m_bIsEnd;
    std::atomic<bool> m_bIsStarted;
    std::thread m_thread;
};

UDPRecv::UDPRecv(DFNetWork* poDFNetWork) : m_poDFNetWork(poDFNetWork), m_iSockFD(-1), m_bIsEnd(true), m_bIsStarted(false) {}

UDPRecv::~UDPRecv() {
    if (m_iSockFD != -1) {
        close(m_iSockFD);
    }
}

void UDPRecv::Stop() {
    m_bIsEnd = true;
    if (m_thread.joinable()) {
        m_thread.join();
    }
}

int UDPRecv::Init(int iPort) {
    m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
    if (m_iSockFD == -1) {
        return -1;
    }

    sockaddr_in serverAddr;
    memset(&serverAddr, 0, sizeof(serverAddr));
    serverAddr.sin_family = AF_INET;
    serverAddr.sin_addr.s_addr = INADDR_ANY;
    serverAddr.sin_port = htons(iPort);

    if (bind(m_iSockFD, (sockaddr*)&serverAddr, sizeof(serverAddr)) == -1) {
        close(m_iSockFD);
        m_iSockFD = -1;
        return -1;
    }

    m_bIsStarted = true;
    m_bIsEnd = false;
    m_thread = std::thread(&UDPRecv::run, this);
    return 0;
}

void UDPRecv::run() {
    struct pollfd fds[1];
    fds[0].fd = m_iSockFD;
    fds[0].events = POLLIN;

    while (!m_bIsEnd) {
        int ret = poll(fds, 1, 100); // 100ms timeout
        if (ret > 0 && (fds[0].revents & POLLIN)) {
            char buffer[65536];
            sockaddr_in clientAddr;
            socklen_t addrLen = sizeof(clientAddr);
            ssize_t bytesReceived = recvfrom(m_iSockFD, buffer, sizeof(buffer), 0, (sockaddr*)&clientAddr, &addrLen);
            if (bytesReceived > 0) {
                m_poDFNetWork->ProcessMessage(buffer, static_cast<size_t>(bytesReceived), clientAddr);
            }
        }
    }
}

