bool DownloadTask::run() {
    if (!m_curl) {
        m_error = "Failed to initialize CURL";
        return false;
    }

    m_outputTmp = m_output + ".tmp";
    m_file.open(m_outputTmp, std::ios::binary | std::ios::trunc);
