        return false;
    }

    bool success = false;
    for (unsigned int i = 0; i <= m_retries; ++i) {
        success = downloadFile(m_url);
        if (success) break;
        if (i < m_retries && !m_urlBackup.empty()) {
            m_url = m_urlBackup;
        }
    }

    if (success) {
        m_file.close();
        if (rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
            m_error = "Failed to rename temporary file";
            success = false;
        }
    } else {
        m_file.close();
        if (!m_default.empty()) {
            if (rename(m_outputTmp.c_str(), m_default.c_str()) != 0) {
