        data.name = "Unnamed";
        m_connections[hdl] = data;
        std::cout << "New connection: Session ID " << data.session_id << std::endl;
    }

    void on_close(wss::connection_hdl hdl) {
        auto it = m_connections.find(hdl);
        if (it != m_connections.end()) {
            std::cout << "Connection closed: Session ID " << it->second.session_id << std::endl;
            m_connections.erase(it);
        }
    }

    void on_message(wss::connection_hdl hdl, wss::server::message_ptr msg) {
        auto& data = get_data_from_hdl(hdl);
        if (msg->get_payload().substr(0, 5) == "NAME:") {
            data.name = msg->get_payload().substr(5);
            std::cout << "Client " << data.session_id << " renamed to " << data.name << std::endl;
        } else {
            std::cout << "Session ID " << data.session_id << " (" << data.name << "): " << msg->get_payload() << std::endl;
        }
    }

    connection_data& get_data_from_hdl(wss::connection_hdl hdl) {
        auto it = m_connections.find(hdl);
        if (it == m_connections.end()) {
            throw std::runtime_error("Connection not found");
        }
        return it->second;
    }

public:
    print_server() : m_next_sessionid(1) {
        m_server.init_asio();
        m_server.set_open_handler([this](wss::connection_hdl hdl) { this->on_open(hdl); });
        m_server.set_close_handler([this](wss::connection_hdl hdl) { this->on_close(hdl); });
        m_server.set_message_handler([this](wss::connection_hdl hdl, wss::server::message_ptr msg) { this->on_message(hdl, msg); });
    }

    void run(uint16_t port) {
        m_server.listen(port);
        m_server.start_accept();
        m_server.run();
    }
};

