    void init() {
        // Initialize plain server
        endpoint_plain_.set_message_handler([this](server_plain::connection_hdl hdl, server_plain::message_ptr msg) {
            on_message(&endpoint_plain_, hdl, msg);
        });

        endpoint_plain_.init_asio();
        endpoint_plain_.listen(80);
