void pipe_communication() {
    int pipefd[2];
    if (pipe(pipefd) == -1) {
        std::cerr << "pipe create error" << std::endl;
        return;
    }

    pid_t pid = fork();
    if (pid == -1) {
        std::cerr << "fork error" << std::endl;
        close(pipefd[0]);
        close(pipefd[1]);
        return;
    }

    if (pid == 0) {
        // Child process
        close(pipefd[1]);
        char buffer[100];
        ssize_t bytes_read = read(pipefd[0], buffer, sizeof(buffer) - 1);
        if (bytes_read > 0) {
            buffer[bytes_read] = '\0';
            std::cout << "Read data from the pipe is [" << buffer << "]" << std::endl;
        }
        close(pipefd[0]);
    } else {
