#include <nlohmann/json.hpp>

namespace beast = boost::beast;
namespace http = beast::http;
namespace websocket = beast::websocket;
namespace net = boost::asio;
namespace ssl = boost::asio::ssl;
using tcp = boost::asio::ip::tcp;
using udp = boost::asio::ip::udp;
using json = nlohmann::json;

class WebOsClient::Impl : public std::enable_shared_from_this<WebOsClient::Impl> {
private:
    net::io_context& ioc_;
    ssl::context& ctx_;
    Device device_settings_;
    std::shared_ptr<Logging> log_;
    tcp::resolver resolver_;
    std::optional<websocket::stream<beast::ssl_stream<beast::tcp_stream>>> ws_;
    std::optional<websocket::stream<beast::tcp_stream>> ws_tcp_;
    beast::flat_buffer buffer_;
    std::string host_;
    int socket_status_ = SOCKET_DISCONNECTED;

    void run() {
        socket_status_ = SOCKET_CONNECTING;
        if(device_settings_.ssl)
            ws_.emplace(resolver_.get_executor(), ctx_);
        else
            ws_tcp_.emplace(resolver_.get_executor());
        host_ = device_settings_.ip;
        resolver_.async_resolve(host_.c_str(), device_settings_.ssl ? PORT_SSL : PORT,
            beast::bind_front_handler(&Impl::onResolve, shared_from_this()));
    }

    void onResolve(beast::error_code ec, tcp::resolver::results_type results) {
        if (ec) return onError(ec, "onResolve");
        socket_status_ = SOCKET_CONNECTING;
        if(device_settings_.ssl) {
            beast::get_lowest_layer(*ws_).expires_after(std::chrono::milliseconds(TIMER_ASYNC_TIMEOUT));
            beast::get_lowest_layer(*ws_).async_connect(results,
                beast::bind_front_handler(&Impl::onConnect, shared_from_this()));
        } else {
            beast::get_lowest_layer(*ws_tcp_).expires_after(std::chrono::milliseconds(TIMER_ASYNC_TIMEOUT));
            beast::get_lowest_layer(*ws_tcp_).async_connect(results,
                beast::bind_front_handler(&Impl::onConnect, shared_from_this()));
        }
    }

    void onConnect(beast::error_code ec, tcp::resolver::results_type::endpoint_type ep) {
        if (ec) return onError(ec, "onConnect");
        socket_status_ = SOCKET_CONNECTING;
        if(device_settings_.ssl) {
            beast::get_lowest_layer(*ws_).expires_after(std::chrono::milliseconds(TIMER_ASYNC_TIMEOUT));
            if (!SSL_set_tlsext_host_name(ws_->next_layer().native_handle(), device_settings_.ip.c_str())) {
                ec = beast::error_code(static_cast<int>(::ERR_get_error()), net::error::get_ssl_category());
                return onError(ec, "Failed to set SNI hostname");
            }
            host_ += ':' + std::to_string(ep.port());
            ws_->next_layer().async_handshake(ssl::stream_base::client,
                beast::bind_front_handler(&Impl::onSSLhandshake, shared_from_this()));
        } else {
            beast::get_lowest_layer(*ws_tcp_).expires_after(std::chrono::milliseconds(TIMER_ASYNC_TIMEOUT));
            ws_tcp_->set_option(websocket::stream_base::timeout::suggested(beast::role_type::client));
            ws_tcp_->set_option(websocket::stream_base::decorator(
                [](websocket::request_type& req) {
                    req.set(http::field::user_agent,
                        std::string(BOOST_BEAST_VERSION_STRING) + " websocket-client-async");
                }));
            host_ += ':' + std::to_string(ep.port());
            ws_tcp_->async_handshake(host_, "/",
                beast::bind_front_handler(&Impl::onWinsockHandshake, shared_from_this()));
        }
    }

    void onSSLhandshake(beast::error_code ec) {
        if (ec) return onError(ec, "onSSLhandshake");
        socket_status_ = SOCKET_CONNECTING;
        beast::get_lowest_layer(*ws_).expires_never();
        ws_->set_option(websocket::stream_base::timeout::suggested(beast::role_type::client));
        ws_->set_option(websocket::stream_base::decorator(
            [](websocket::request_type& req) {
                req.set(http::field::user_agent,
                    std::string(BOOST_BEAST_VERSION_STRING) + " websocket-client-async-ssl");
            }));
        ws_->async_handshake(host_, "/",
            beast::bind_front_handler(&Impl::onWinsockHandshake, shared_from_this()));
    }

    void onWinsockHandshake(beast::error_code ec) {
        if (ec) return onError(ec, "onWinsockHandshake");
        socket_status_ = SOCKET_CONNECTED;
        send(webos_handshake_, "webOS handshake");
    }

    void send(std::string data, std::string log_message = "") {
        if(device_settings_.ssl) {
            ws_->async_write(net::buffer(data),
                beast::bind_front_handler(&Impl::onWrite, shared_from_this()));
        } else {
            ws_tcp_->async_write(net::buffer(data),
                beast::bind_front_handler(&Impl::onWrite, shared_from_this()));
        }
    }

    void onWrite(beast::error_code ec, std::size_t bytes_transferred) {
        boost::ignore_unused(bytes_transferred);
        if (ec) return onError(ec, "onWrite");
        read();
    }

    void read() {
        if(device_settings_.ssl)
            ws_->async_read(buffer_,
                beast::bind_front_handler(&Impl::onRead, shared_from_this()));
        else
            ws_tcp_->async_read(buffer_,
                beast::bind_front_handler(&Impl::onRead, shared_from_this()));
    }

    void onRead(beast::error_code ec, std::size_t bytes_transferred) {
        boost::ignore_unused(bytes_transferred);
        if (ec) return onError(ec, "onRead");
        socket_status_ = SOCKET_CONNECTED;
        // Process received data here
        buffer_.consume(buffer_.size());
        read();
    }

    void onError(beast::error_code& ec, std::string err) {
        DEBUG("%1% (%2%)", ec.message(), err);
        socket_status_ = SOCKET_DISCONNECTED;
    }

public:
    explicit Impl(net::io_context& ioc, ssl::context& ctx, Device& settings, Logging& log)
        : ioc_(ioc), ctx_(ctx), device_settings_(settings), log_(std::make_shared<Logging>(log)),
          resolver_(net::make_strand(ioc)) {}

    void enqueueWork(Work& work) {
        net::dispatch(resolver_.get_executor(),
            [unit = work, self = shared_from_this()]() mutable {
                self->workQueue_.emplace_back(std::move(unit));
                self->startNextWork();
            });
    }

    void close() {
        net::dispatch(resolver_.get_executor(),
            beast::bind_front_handler(&Impl::doClose, shared_from_this()));
    }
};

