                ws_tcp_->write(net::buffer(data), std::bind(&Impl::onWrite, this, std::placeholders::_1, std::placeholders::_2));
            }
        }

        void onWrite(beast::error_code ec, std::size_t bytes_transferred) {
            if (ec) onError(ec, "write failed");
        }

        void read() {
            if (device_settings_.use_ssl) {
                ws_->async_read(buffer_, std::bind(&Impl::onRead, this, std::placeholders::_1, std::placeholders::_2));
            } else {
                ws_tcp_->async_read(buffer_, std::bind(&Impl::onRead, this, std::placeholders::_1, std::placeholders::_2));
            }
        }

        void onRead(beast::error_code ec, std::size_t bytes_transferred) {
            if (ec) return onError(ec, "read failed");
            std::string message = beast::buffers_to_string(buffer_.data());
            buffer_.consume(buffer_.size());
            read();
        }

        void onError(beast::error_code& ec, std::string message) {
            log_->log(message + ": " + ec.message());
            close();
        }

        void enqueueWork(Work& work) {
            ioc_.post(std::bind(&Work::execute, &work));
        }

        void close() {
            if (device_settings_.use_ssl) {
                ws_->next_layer().shutdown();
                ws_->next_layer().lowest_layer().close();
            } else {
                ws_tcp_->next_layer().shutdown();
                ws_tcp_->next_layer().lowest_layer().close();
            }
            socket_status_ = 0; // disconnected
        }

    public:
        Impl(net::io_context& ioc, ssl::context& ctx, Device& device, Logging& log)
            : ioc_(ioc), ctx_(ctx), device_settings_(device), log_(&log), resolver_(ioc), host_(""), socket_status_(0) {
            if (device.use_ssl) {
                ws_ = websocket::stream<beast::ssl_stream<beast::tcp_stream>>(ioc, ctx);
            } else {
                ws_tcp_ = websocket::stream<beast::tcp_stream>(ioc);
            }
        }

        ~Impl() {
            close();
        }
    };
};
