        [this](boost::system::error_code ec, std::size_t /*length*/) {
            if (!ec) {
                write_msgs_.pop_front();
                if (!write_msgs_.empty()) {
                    do_write();
                }
            }
        });
}

chat_client::chat_client(boost::asio::io_context& io_context, const tcp::resolver::results_type& endpoints) : io_context_(io_context), socket_(io_context), read_msg_(), write_msgs_() {
    do_connect(endpoints);
}

void chat_client::write(const chat_message& msg) {
    if (socket_.is_open()) {
        write_msgs_.push_back(msg);
        if (write_msgs_.size() == 1) {
            do_write();
        }
    }
}

void chat_client::close() {
    if (socket_.is_open()) {
        boost::system::error_code ignored_error;
        socket_.shutdown(tcp::socket::shutdown_both, ignored_error);
        socket_.close();
    }
}

void chat_client::do_connect(const tcp::resolver::results_type& endpoints) {
    boost::asio::async_connect(socket_, endpoints,
        [this](boost::system::error_code ec, tcp::endpoint) {
            if (!ec) {
                do_read_header();
            }
        });
}

void chat_client::do_read_header() {
    boost::asio::async_read(socket_,
        boost::asio::buffer(&read_msg_.header_, sizeof(read_msg_.header_)),
        [this](boost::system::error_code ec, std::size_t /*length*/) {
            if (!ec && read_msg_.header_.length > 0) {
                do_read_body();
            }
        });
}

void chat_client::do_read_body() {
    boost::asio::async_read(socket_,
        boost::asio::buffer(read_msg_.body_, read_msg_.header_.length),
        [this](boost::system::error_code ec, std::size_t /*length*/) {
            if (!ec) {
                // Process the received message
                do_read_header();
            }
        });
}

void chat_client::do_write() {
    if (!write_msgs_.empty()) {
        boost::asio::async_write(socket_,
            boost::asio::buffer(write_msgs_.front().data(), write_msgs_.front().length()),
            [this](boost::system::error_code ec, std::size_t /*length*/) {
                if (!ec) {
                    write_msgs_.pop_front();
                    if (!write_msgs_.empty()) {
                        do_write();
                    }
                }
            });
    }
}
