#include <iostream>
#include <string>
#include <vector>
#include <system_error>
#include <cstring>
#include <unistd.h>
#include <arpa/inet.h>
#include <sys/socket.h>
#include <netinet/in.h>
#include <thread>
#include <chrono>
#include <mutex>

class LinHttpHandler {
public:
    class SocketCloser {
    private:
        int sockFd;
    public:
        explicit SocketCloser(int fd) : sockFd(fd) {}
        ~SocketCloser() {
            if (sockFd != -1) {
                close(sockFd);
            }
        }
        int get() const { return sockFd; }
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to create TCP socket");
        }
        SocketCloser closer(sock);

        sockaddr_in serverAddr{};
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::generic_category(), "Invalid address");
        }

        if (connect(sock, reinterpret_cast<sockaddr*>(&serverAddr), sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Connection failed");
        }

        if (send(sock, msg.c_str(), msg.size(), 0) < 0) {
            throw std::system_error(errno, std::generic_category(), "Send failed");
        }

        char buffer[4096];
        std::string response;
        ssize_t bytes;

        while ((bytes = recv(sock, buffer, sizeof(buffer), 0)) > 0) {
            response.append(buffer, bytes);
        }

        if (bytes < 0) {
            throw std::system_error(errno, std::generic_category(), "Receive failed");
        }

        return response;
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to create UDP socket");
        }
        SocketCloser closer(sock);

        int broadcastEnable = 1;
        if (setsockopt(sock, SOL_SOCKET, SO_BROADCAST, &broadcastEnable, sizeof(broadcastEnable)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Setsockopt failed");
        }

        sockaddr_in multicastAddr{};
        multicastAddr.sin_family = AF_INET;
        multicastAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &multicastAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::generic_category(), "Invalid multicast address");
        }

        if (sendto(sock, msg.c_str(), msg.size(), 0, reinterpret_cast<sockaddr*>(&multicastAddr), sizeof(multicastAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Multicast send failed");
        }

        sockaddr_in clientAddr{};
        socklen_t addrLen = sizeof(clientAddr);
        std::vector<std::string> responses;
        std::mutex responseMutex;

        auto receiveThread = std::thread([=, &responses, &responseMutex, &clientAddr, &addrLen]() mutable {
            char buffer[4096];
            while (true) {
                ssize_t bytes = recvfrom(sock, buffer, sizeof(buffer), 0, reinterpret_cast<sockaddr*>(&clientAddr), &addrLen);
                if (bytes < 0) {
                    break;
                }
                std::string resp(buffer, bytes);
                std::lock_guard<std::mutex> lock(responseMutex);
                responses.push_back(resp);
            }
        });

        std::this_thread::sleep_for(timeout);
        receiveThread.detach();

        return responses;
    }
};
