enum class ErrorCode {
    SUCCESS,
    CONNECTION_FAILED,
    TIMEOUT,
    INVALID_ADDRESS
};

class ClientSocket {
public:
    ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
        : threadCtx_(threadCtx), dest_(dest), sa_(), socket_(std::make_unique<WdtSocket>()), peerIp_("") {
        memset(&sa_, 0, sizeof(sa_));
        sa_.ai_family = AF_UNSPEC;
        sa_.ai_socktype = SOCK_STREAM;
        sa_.ai_protocol = IPPROTO_TCP;
    }

    ErrorCode connect() {
        struct addrinfo* res = nullptr;
        struct addrinfo hints = {};
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;
        hints.ai_protocol = IPPROTO_TCP;

        int status = getaddrinfo(dest_.c_str(), std::to_string(port).c_str(), &hints, &res);
        if (status != 0 || !res) {
            return ErrorCode::INVALID_ADDRESS;
        }

        for (struct addrinfo* rp = res; rp != nullptr; rp = rp->ai_next) {
            socket_->fd = socket(rp->ai_family, rp->ai_socktype, rp->ai_protocol);
            if (socket_->fd == -1) continue;

            fcntl(socket_->fd, F_SETFL, O_NONBLOCK);

            if (::connect(socket_->fd, rp->ai_addr, rp->ai_addrlen) == 0) {
                setSendBufferSize();
                peerIp_ = getAddressFromAddrinfo(rp);
                freeaddrinfo(res);
                return ErrorCode::SUCCESS;
            } else if (errno != EINPROGRESS) {
                closeConnection();
                continue;
            }

            // Wait for connection to complete with timeout
            std::this_thread::sleep_for(std::chrono::seconds(5));
            socklen_t optlen = sizeof(int);
            int optval;
            if (getsockopt(socket_->fd, SOL_SOCKET, SO_ERROR, &optval, &optlen) == -1 || optval != 0) {
                closeConnection();
                continue;
            }

            setSendBufferSize();
            peerIp_ = getAddressFromAddrinfo(rp);
            freeaddrinfo(res);
            return ErrorCode::SUCCESS;
        }

        freeaddrinfo(res);
        return ErrorCode::CONNECTION_FAILED;
    }

    const std::string& getPeerIp() {
        return peerIp_;
    }

    void setSendBufferSize() {
        if (threadCtx_.options.sendBufferSize > 0) {
            setsockopt(socket_->fd, SOL_SOCKET, SO_SNDBUF, &threadCtx_.options.sendBufferSize, sizeof(threadCtx_.options.sendBufferSize));
        }
    }

private:
    void closeConnection() {
        if (socket_->fd >= 0) {
            shutdown(socket_->fd, SHUT_RDWR);
            socket_->fd = -1;
        }
    }

    std::string getAddressFromAddrinfo(const struct addrinfo* ai) {
        char ip[INET6_ADDRSTRLEN] = {0};
        void* addr;
        if (ai->ai_family == AF_INET) {
            addr = &((struct sockaddr_in*)ai->ai_addr)->sin_addr;
        } else {
            addr = &((struct sockaddr_in6*)ai->ai_addr)->sin6_addr;
        }
        inet_ntop(ai->ai_family, addr, ip, sizeof(ip));
        return std::string(ip);
    }

    ThreadCtx& threadCtx_;
    std::string dest_;
    int port_ = 0;
    struct addrinfo sa_;
    std::unique_ptr<WdtSocket> socket_;
    std::string peerIp_;
};

