#include <sys/socket.h>
#include <sys/un.h>
#include <unistd.h>
#include <thread>
#include <atomic>
#include <mutex>
#include <condition_variable>
#include <unordered_map>
#include <string>
#include <memory>
#include <vector>

class Stats {
public:
    static Stats& get(const std::string& stats_socket_path) {
        static Stats instance(stats_socket_path);
        return instance;
    }

    static std::unique_ptr<Stats> get_for_unittest(const std::string& stats_socket_path) {
        return std::make_unique<Stats>(stats_socket_path);
    }

    static bool isInit() {
        return isInitInternal();
    }

    static bool init(const std::string& stats_socket_path) {
        if (!isInitInternal()) {
            new Stats(stats_socket_path);
            return true;
        }
        return false;
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] += val;
        return stats_[key];
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return stats_[key];
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

private:
    std::string stats_socket_path_;
    int sockfd_;
    sockaddr_un serv_addr_;
    std::thread stats_thread_;
    std::atomic<bool> statsThreadRunning_;
    std::mutex stats_mutex_;
    std::mutex thread_mutex_;
    std::condition_variable thread_exited_;
    int thread_count_;
    std::unordered_map<std::string, int> stats_;

    static bool& isInitInternal() {
        static bool initialized = false;
        return initialized;
    }

    Stats(const std::string& stats_socket_path)
        : stats_socket_path_(stats_socket_path),
          sockfd_(-1),
          statsThreadRunning_(false),
          thread_count_(0) {
        startSocket();
        isInitInternal() = true;
    }

    ~Stats() {
        if (sockfd_ != -1) {
            close(sockfd_);
            unlink(stats_socket_path_.c_str());
        }
        statsThreadRunning_ = false;
        if (stats_thread_.joinable()) {
            stats_thread_.join();
        }
    }

    bool startSocket() {
        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ < 0) {
            return false;
        }

        memset(&serv_addr_, 0, sizeof(serv_addr_));
        serv_addr_.sun_family = AF_UNIX;
        strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path) - 1);

        unlink(stats_socket_path_.c_str());

        if (bind(sockfd_, reinterpret_cast<struct sockaddr*>(&serv_addr_), sizeof(serv_addr_)) < 0) {
            close(sockfd_);
            return false;
        }

        if (listen(sockfd_, 5) < 0) {
            close(sockfd_);
            return false;
        }

        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);
        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            struct sockaddr_un cli_addr;
            socklen_t clilen = sizeof(cli_addr);
            int client_sockfd = accept(sockfd_, reinterpret_cast<struct sockaddr*>(&cli_addr), &clilen);
            if (client_sockfd < 0) {
                continue;
            }

            {
                std::lock_guard<std::mutex> lock(thread_mutex_);
                ++thread_count_;
            }

            std::thread([this, client_sockfd]() {
                processMsg(client_sockfd);
                {
                    std::lock_guard<std::mutex> lock(thread_mutex_);
                    --thread_count_;
                }
                thread_exited_.notify_one();
                close(client_sockfd);
            }).detach();
        }
    }

    void processMsg(int sockfd) {
        char buffer[256];
        ssize_t n = read(sockfd, buffer, sizeof(buffer) - 1);
        if (n <= 0) {
            return;
        }

        buffer[n] = '\0';
        std::string request(buffer);

        std::string response;

        if (request == "get") {
            std::unordered_map<std::string, int> data;
            {
                std::lock_guard<std::mutex> lock(stats_mutex_);
                data = stats_;
            }
            for (const auto& [key, value] : data) {
                response += key + ":" + std::to_string(value) + "\n";
            }
        } else if (request.find("increment ") == 0) {
            size_t pos = request.find(' ');
            if (pos != std::string::npos && pos + 1 < request.size()) {
                std::string key = request.substr(pos + 1);
                int val = 1;
                try {
                    val = std::stoi(key);
                    key = "default_key";
                } catch (...) {
                    size_t colon_pos = key.find(':');
                    if (colon_pos != std::string::npos) {
                        std::string temp_key = key.substr(0, colon_pos);
                        std::string temp_val = key.substr(colon_pos + 1);
                        try {
                            key = temp_key;
                            val = std::stoi(temp_val);
                        } catch (...) {
                            response = "Invalid format";
                            write(sockfd, response.c_str(), response.size());
                            return;
                        }
                    } else {
                        val = 1;
                    }
                }

                {
                    std::lock_guard<std::mutex> lock(stats_mutex_);
                    stats_[key] += val;
                }
                response = "Incremented " + key + " by " + std::to_string(val);
            } else {
                response = "Invalid command";
            }
        } else if (request == "reset") {
            {
                std::lock_guard<std::mutex> lock(stats_mutex_);
                stats_.clear();
            }
            response = "All statistics reset";
        } else {
            response = "Unknown command";
        }

        write(sockfd, response.c_str(), response.size());
    }
};
