        acceptor_.async_accept(*socket,
            boost::bind(&server::handle_accept, this,
                boost::asio::placeholders::error, socket));
    }

    void handle_accept(const boost::system::error_code& ec, tcp_socket_ptr socket) {
        if (!ec) {
            char buffer[1024];
            control_request_ptr request(new std::string());
            socket->async_read_some(asio::buffer(buffer),
                boost::bind(&server::handle_control_request, this,
                    boost::asio::placeholders::error, socket, request));
        }
        start_accept();
    }

    void handle_control_request(const boost::system::error_code& ec, tcp_socket_ptr socket, control_request_ptr request) {
        if (!ec) {
            timer_ptr delay_timer(new asio::steady_timer(socket->get_executor().context()));
            delay_timer->expires_after(std::chrono::seconds(1));
            delay_timer->async_wait(boost::bind(&server::handle_control_request_timer, this, socket, request, delay_timer));
