        if (*request == "SUBSCRIBE") {
            udp::endpoint endpoint = socket->remote_endpoint();
            subscribers_.insert(endpoint);
        }
        socket->close();
    }

    void handle_timer() {
        timer_.expires_after(std::chrono::seconds(5));
        timer_.async_wait([this](const boost::system::error_code&) {
            broadcast_frame();
            handle_timer();
        });
