#include <curl/curl.h>
#include <string>
#include <fstream>
#include <sstream>
#include <ctime>
#include <cstdlib>
#include <cerrno>
#include <cstring>

class DownloadTask {
public:
    static void initialize() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    DownloadTask(const std::string& url, const std::string& output,
                 const std::string& defaultFile, time_t mtime, const std::string& name = "")
        : m_url(url), m_output(output), m_outputTmp(output + ".tmp"),
          m_default(defaultFile), m_mtime(mtime), m_retries(0),
          m_name(name), m_userAgent("libcurl-agent/1.0") {}

    bool run() {
        if (downloadFile(m_url)) {
            return finalizeDownload();
        }

        if (!m_urlBackup.empty() && downloadFile(m_urlBackup)) {
            return finalizeDownload();
        }

        setError("Failed to download from any URL");
        return false;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    void setError(const std::string& error) {
        m_error = error;
    }

    size_t writeData(void* buffer, size_t size, size_t nmemb) {
        if (!m_file.is_open()) {
            m_file.open(m_outputTmp, std::ios::binary | std::ios::out);
            if (!m_file.is_open()) {
                setError("Failed to open temporary file for writing");
                return 0;
            }
        }

        size_t totalSize = size * nmemb;
        m_file.write(static_cast<char*>(buffer), totalSize);
        return totalSize;
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            setError("Failed to initialize libcurl session");
            return false;
        }

        m_file.close();
        if (std::remove(m_outputTmp.c_str()) != 0 && errno != ENOENT) {
            setError("Failed to remove existing temporary file");
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            setError(std::string("libcurl error: ") + curl_easy_strerror(res));
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_cleanup(curl);
        return true;
    }

    bool finalizeDownload() {
        if (!m_file.is_open()) {
            m_file.open(m_outputTmp, std::ios::binary | std::ios::in);
            if (!m_file.is_open()) {
                setError("Failed to open temporary file for finalization");
                return false;
            }
        }

        m_file.seekg(0, std::ios::end);
        long fileSize = m_file.tellg();
        m_file.seekg(0, std::ios::beg);

        if (fileSize <= 0) {
            setError("Downloaded file is empty");
            return false;
        }

        m_file.close();

        if (std::rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
            setError("Failed to rename temporary file to output file");
            return false;
        }

        struct utimbuf times;
        times.actime = m_mtime;
        times.modtime = m_mtime;
        if (utime(m_output.c_str(), &times) != 0) {
            setError("Failed to set modification time on output file");
            return false;
        }

        return true;
    }
};
