#include <condition_variable>

typedef websocketpp::server<websocketpp::config::asio> server;
typedef server::connection_hdl connection_hdl;
typedef std::set<connection_hdl> con_list;

enum class action_type {
    SUBSCRIBE,
    UNSUBSCRIBE,
    MESSAGE
