class broadcast_server {
public:
    broadcast_server() {
        m_server.init_asio();
        m_server.set_open_handler(std::bind(&broadcast_server::on_open, this, std::placeholders::_1));
        m_server.set_close_handler(std::bind(&broadcast_server::on_close, this, std::placeholders::_1));
        m_server.set_message_handler(std::bind(&broadcast_server::on_message, this, std::placeholders::_1, std::placeholders::_2));
    }

    void run(uint16_t port) {
        m_server.listen(port);
        m_server.start_accept();

        std::thread t([this](){ m_server.run(); });
        process_messages();

        t.join();
    }

private:
    void on_open(connection_hdl hdl) {
        std::lock_guard<std::mutex> lock(m_action_lock);
        m_actions.push({action_type::SUBSCRIBE, hdl, nullptr});
        m_action_cond.notify_one();
    }

    void on_close(connection_hdl hdl) {
        std::lock_guard<std::mutex> lock(m_action_lock);
        m_actions.push({action_type::UNSUBSCRIBE, hdl, nullptr});
        m_action_cond.notify_one();
    }

    void on_message(connection_hdl hdl, server::message_ptr msg) {
        std::lock_guard<std::mutex> lock(m_action_lock);
        m_actions.push({action_type::MESSAGE, hdl, msg});
        m_action_cond.notify_one();
    }

    void process_messages() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_action_lock);
            m_action_cond.wait(lock, [this](){ return !m_actions.empty(); });

            action a = m_actions.front();
            m_actions.pop();
            lock.unlock();

            if (a.type == action_type::SUBSCRIBE) {
                std::lock_guard<std::mutex> conn_lock(m_connection_lock);
                m_connections.insert(a.hdl);
            } else if (a.type == action_type::UNSUBSCRIBE) {
                std::lock_guard<std::mutex> conn_lock(m_connection_lock);
                m_connections.erase(a.hdl);
            } else if (a.type == action_type::MESSAGE) {
                std::lock_guard<std::mutex> conn_lock(m_connection_lock);
                for (auto const& conn : m_connections) {
                    m_server.send(conn, a.msg->get_payload(), a.msg->get_opcode());
                }
            }
        }
    }

    server m_server;
    con_list m_connections;
    std::queue<action> m_actions;
    std::mutex m_action_lock;
    std::mutex m_connection_lock;
    std::condition_variable m_action_cond;
};

