void pipe_communication() {
    int fd[2];
    pid_t pid;
    const char* msg = "Hello from parent";
    char buffer[100];

    if (pipe(fd) == -1) {
        std::cerr << "pipe create error" << std::endl;
        return;
    }

    pid = fork();

    if (pid < 0) {
        close(fd[0]);
        close(fd[1]);
        return;
    } else if (pid == 0) {
        close(fd[1]);

        ssize_t count = read(fd[0], buffer, sizeof(buffer));
        if (count > 0) {
            std::cout << "Read data from the pipe is " << buffer << std::endl;
        }
        close(fd[0]);
    } else {
        close(fd[0]);

        std::cout << "Write data " << msg << std::endl;
        write(fd[1], msg, strlen(msg) + 1);
