class Task {
private:
    std::string m_url;
    std::string m_output;
    FILE* m_file;

public:
    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output), m_file(nullptr) {}

    void run() {
        CURL* curl = curl_easy_init();
        if (!curl) return;

        m_file = fopen(m_output.c_str(), "wb");
        if (!m_file) {
            curl_easy_cleanup(curl);
            return;
        }

        curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            std::cerr << "Download failed: " << curl_easy_strerror(res) << std::endl;
        }

        fclose(m_file);
        curl_easy_cleanup(curl);
    }

    static size_t writeData(void* buffer, size_t size, size_t nmemb, void* userp) {
        size_t realsize = size * nmemb;
        Task* task = reinterpret_cast<Task*>(userp);
        if (task->m_file) {
            return fwrite(buffer, 1, realsize, task->m_file);
        }
        return 0;
    }
};

class DownloadPool {
private:
    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    std::condition_variable m_cv;
    bool m_noMoreTask = false;

    void run() {
        while (true) {
            Task task;
            {
                std::unique_lock<std::mutex> lock(m_mtx);
                m_cv.wait(lock, [this] { return !m_queue.empty() || m_noMoreTask; });

                if (m_queue.empty()) break;

                task = std::move(m_queue.front());
                m_queue.pop();
            }

            task.run();
        }
    }

public:
    DownloadPool(size_t numThreads = std::thread::hardware_concurrency()) {
        curl_global_init(CURL_GLOBAL_DEFAULT);
        for (size_t i = 0; i < numThreads; ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        setNoMoreTask();
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_urls.insert(url);
            m_queue.emplace(url, output);
            m_cv.notify_one();
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
        m_cv.notify_all();
    }

    void finishAndWaitForExit() {
        for (std::thread& thread : m_threads) {
            if (thread.joinable()) {
                thread.join();
            }
        }
    }
};
