class posix_chat_client {
public:
  posix_chat_client(boost::asio::io_context& io_context,
                    const tcp::resolver::results_type& endpoints)
    : io_context_(io_context),
      socket_(io_context),
      input_(io_context, ::fileno(stdin)),
