    do_read_input();
    do_read_header();
  }

  void do_connect(const tcp::resolver::results_type& endpoints) {
    boost::asio::async_connect(
        socket_, endpoints,
        boost::bind(&posix_chat_client::handle_connect, this,
                    boost::asio::placeholders::error));
