    if (!error) {
      do_write_output();
    } else {
      close();
    }
  }

  void do_write_output() {
    boost::asio::async_write(output_,
                             boost::asio::buffer(read_msg_.body(), read_msg_.body_length()),
                             boost::bind(&posix_chat_client::handle_write_output, this,
                                         boost::asio::placeholders::error));
  }

  void handle_write_output(const boost::system::error_code& error) {
    if (!error) {
      read_msg_ = chat_message();
      do_read_header();
    } else {
      close();
    }
  }

  void do_read_input() {
    boost::asio::async_read_until(input_, input_buffer_, '\n',
                                  boost::bind(&posix_chat_client::handle_read_input, this,
                                              boost::asio::placeholders::error,
                                              boost::asio::placeholders::bytes_transferred));
  }

  void handle_read_input(const boost::system::error_code& error, std::size_t bytes_transferred) {
    if (!error) {
      std::istream is(&input_buffer_);
      is.read(write_msg_.body(), write_msg_.body_length());
      std::size_t length = is.gcount();
      write_msg_.body_length(static_cast<std::size_t>(length));
      do_write_message();
      do_read_input();
    } else {
      close();
    }
  }

  void do_write_message() {
    boost::asio::async_write(socket_,
                             boost::asio::buffer(write_msg_.data(), write_msg_.length()),
                             boost::bind(&posix_chat_client::handle_write_message, this,
                                         boost::asio::placeholders::error));
  }

  void handle_write_message(const boost::system::error_code& error) {
    if (!error) {
      write_msg_ = chat_message();
    } else {
      close();
    }
  }

  void close() {
    socket_.close();
    input_.close();
    output_.close();
  }

private:
  boost::asio::io_context& io_context_;
  tcp::socket socket_;
  posix::stream_descriptor input_;
  posix::stream_descriptor output_;
  chat_message read_msg_;
  chat_message write_msg_;
  boost::asio::streambuf input_buffer_;
};

