ClientSocket::ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval) : dest_(dest), threadCtx_(threadCtx), socket_(std::make_unique<WdtSocket>()) {
