}

const std::string& ClientSocket::getPeerIp() const {
    return peerIp_;
}

void ClientSocket::setSendBufferSize() {
    if (socket_) {
        int bufferSize = threadCtx_.sendBufferSize;
        setsockopt(socket_->getFd(), SOL_SOCKET, SO_SNDBUF, &bufferSize, sizeof(bufferSize));
    }
}

void ClientSocket::closeConnection() {
    if (socket_) {
        socket_->close();
        cleanup();
    }
}

void ClientSocket::setupAddressInfo() {
    int family = AF_INET;
    if (dest_.find(":") != std::string::npos) {
        family = AF_INET6;
    }

    struct addrinfo hints = {};
    hints.ai_family = family;
    hints.ai_socktype = SOCK_STREAM;
    hints.ai_flags = AI_PASSIVE;

    if (getaddrinfo(dest_.c_str(), "80", &hints, &sa_) != 0) {
        throw std::runtime_error("getaddrinfo failed");
    }
}

void ClientSocket::configureSocket() {
    if (socket_) {
        socket_->connect(&sa_.ai_addr, sa_.ai_addrlen);
    }
}

ErrorCode ClientSocket::connectWithTimeout(int timeoutMs) {
    int socketFd = socket_(sa_.ai_family, sa_.ai_socktype, sa_.ai_protocol);
    if (socketFd < 0) {
        return ErrorCode::SOCKET_ERROR;
    }

    struct timeval tv;
    tv.tv_sec = timeoutMs / 1000;
    tv.tv_usec = (timeoutMs % 1000) * 1000;

    if (setsockopt(socketFd, SOL_SOCKET, SO_RCVTIMEO, &tv, sizeof(tv)) < 0) {
        closeConnection();
        return ErrorCode::TIMEOUT_ERROR;
    }

    if (connect(socketFd, sa_.ai_addr, sa_.ai_addrlen) < 0) {
        closeConnection();
        return ErrorCode::CONNECTION_ERROR;
    }

    if (getpeername(socketFd, &sa_.ai_addr, &sa_.ai_addrlen) < 0) {
        closeConnection();
        return ErrorCode::PEER_NAME_ERROR;
    }

    socket_.reset(new WdtSocket());
    socket_->setFd(socketFd);
    peerIp_ = inet_ntoa(((struct in6_addr*)sa_.ai_addr)->s6_addr);
    return ErrorCode::SUCCESS;
}

void ClientSocket::cleanup() {
    if (sa_) {
        freeaddrinfo(sa_);
    }
}

