class NetworkServer {
public:
    std::vector<RGBController *>& control;
    std::string host;
    unsigned short port_num;
    bool server_online;
    bool server_listening;
    std::vector<NetworkClientInfo *> ServerClients;
    std::mutex ServerClientsMutex;
    SOCKET server_sock[MAXSOCK];
    int socket_count;
    std::thread* ConnectionThread[MAXSOCK];

    NetworkServer(std::vector<RGBController *>& control) : control(control), server_online(false), server_listening(false), socket_count(0) {}

    ~NetworkServer() {
        StopServer();
    }

    void StartServer() {
        WSADATA wsaData;
        if (WSAStartup(MAKEWORD(2, 2), &wsaData) != 0) {
            std::cerr << "WSAStartup failed." << std::endl;
            return;
        }

        sockaddr_in server_addr{};
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port_num);
        inet_pton(AF_INET, host.c_str(), &server_addr.sin_addr);

        server_sock[0] = socket(AF_INET, SOCK_STREAM, 0);
        if (server_sock[0] == INVALID_SOCKET) {
            std::cerr << "Socket creation failed." << std::endl;
            WSACleanup();
            return;
        }

        if (bind(server_sock[0], (sockaddr*)&server_addr, sizeof(server_addr)) == SOCKET_ERROR) {
            std::cerr << "Bind failed." << std::endl;
            closesocket(server_sock[0]);
            WSACleanup();
            return;
        }

        if (listen(server_sock[0], SOMAXCONN) == SOCKET_ERROR) {
            std::cerr << "Listen failed." << std::endl;
            closesocket(server_sock[0]);
            WSACleanup();
            return;
        }

        server_listening = true;
        server_online = true;
        socket_count = 1;
