            NetworkClientInfo* client_info = new NetworkClientInfo();
            client_info->socket = client_sock;
            client_info->ip = inet_ntoa(client_addr.sin_addr);
            client_info->port = ntohs(client_addr.sin_port);
            client_info->connected = true;

