        timeout.tv_sec = 1;
        timeout.tv_usec = 0;

        int result = select(sockfd + 1, &readfds, nullptr, nullptr, &timeout);
        if (result == -1) {
            return -1;
        } else if (result == 0) {
            return 0;
        } else {
            return 1;
        }
