#include <string>
#include <memory>
#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <netdb.h>
#include <unistd.h>
#include <fcntl.h>
#include <errno.h>
#include <sys/select.h>
#include <stdexcept>

class ClientSocket {
public:
    ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
        : threadCtx_(threadCtx), dest_(dest), encryptionParams_(encryptionParams), ivChangeInterval_(ivChangeInterval) {}

    ErrorCode connect() {
        struct addrinfo hints = {};
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;
        hints.ai_protocol = IPPROTO_TCP;

        struct addrinfo* res = nullptr;
        int rc = getaddrinfo(dest_.c_str(), std::to_string(port).c_str(), &hints, &res);
        if (rc != 0) {
            return ErrorCode::ADDRINFO_ERROR;
        }

        for (struct addrinfo* curr = res; curr != nullptr; curr = curr->ai_next) {
            int sockfd = socket(curr->ai_family, curr->ai_socktype, curr->ai_protocol);
            if (sockfd == -1) continue;

            int opt = 1;
            setsockopt(sockfd, SOL_SOCKET, SO_REUSEADDR, &opt, sizeof(opt));
            setsockopt(sockfd, SOL_SOCKET, SO_KEEPALIVE, &opt, sizeof(opt));

            int flags = fcntl(sockfd, F_GETFL, 0);
            fcntl(sockfd, F_SETFL, flags | O_NONBLOCK);

            int connectResult = connect(sockfd, curr->ai_addr, curr->ai_addrlen);
            if (connectResult == 0) {
                break;
            } else if (connectResult == -1 && errno == EINPROGRESS) {
                struct timeval tv = {threadCtx_.getConnectTimeout(), 0};
                fd_set fds;
                FD_ZERO(&fds);
                FD_SET(sockfd, &fds);

                int selectResult = select(sockfd + 1, nullptr, &fds, nullptr, &tv);
                if (selectResult == 0) {
                    close(sockfd);
                    continue;
                } else if (selectResult < 0) {
                    close(sockfd);
                    continue;
                }

                int err;
                socklen_t len = sizeof(err);
                getsockopt(sockfd, SOL_SOCKET, SO_ERROR, &err, &len);
                if (err != 0) {
                    close(sockfd);
                    continue;
                }
                break;
            } else {
                close(sockfd);
                continue;
            }

            sa_ = *curr;
            socket_ = std::make_unique<WdtSocket>(sockfd, encryptionParams_);
