    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout);
    void Disconnect();
    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking);
    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds);

private:
    std::string address;
    u16 port;
    SocketHolder main_socket;
    bool connected;
};

TCPClient::TCPClient(const std::string& address, u16 port) : address(address), port(port), main_socket(-1), connected(false) {}

TCPClient::~TCPClient() {
    Disconnect();
}

bool TCPClient::Connect() {
    main_socket = socket(AF_INET, SOCK_STREAM, 0);
    if (main_socket == -1) return false;

    if (!SetNonBlock(main_socket, true)) {
        close(main_socket);
        return false;
    }

    struct sockaddr_in server_addr;
    memset(&server_addr, 0, sizeof(server_addr));
    server_addr.sin_family = AF_INET;
    server_addr.sin_port = htons(port);
    if (inet_pton(AF_INET, address.c_str(), &server_addr.sin_addr) <= 0) {
        close(main_socket);
        return false;
    }

    if (!ConnectWithTimeout(main_socket, (void*)&server_addr, sizeof(server_addr), 5)) {
        close(main_socket);
        return false;
    }

    connected = true;
    return true;
}

bool TCPClient::ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
    int result = connect(sockFD, (struct sockaddr*)server_addr, server_addr_len);
    if (result == 0) return true;

    if (result == -1 && errno == EINPROGRESS) {
        struct timeval timeout;
        timeout.tv_sec = timeout_seconds;
        timeout.tv_usec = 0;

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        int select_result = select(sockFD + 1, NULL, &write_fds, NULL, &timeout);
        if (select_result == -1) return false;

        if (select_result == 0) return false;

        int so_error;
        socklen_t len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len) < 0) return false;

        return so_error == 0;
    }

    return false;
}

bool TCPClient::Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
    if (!connected || main_socket == -1) return false;

    struct timeval tv;
    tv.tv_sec = static_cast<long>(timeout.count() / 1000000000);
    tv.tv_usec = static_cast<int>(timeout.count() % 1000000000);

    fd_set read_fds;
    FD_ZERO(&read_fds);
    FD_SET(main_socket, &read_fds);

    int select_result = select(main_socket + 1, &read_fds, NULL, NULL, &tv);
    if (select_result == -1) return false;
    if (select_result == 0) return false;

    size_t total_read = 0;
    while (total_read < size) {
        ssize_t bytes_read = read(main_socket, static_cast<char*>(buffer) + total_read, size - total_read);
        if (bytes_read <= 0) return false;
        total_read += bytes_read;
    }

    return true;
}

bool TCPClient::Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
    if (!connected || main_socket == -1) return false;

    struct timeval tv;
    tv.tv_sec = static_cast<long>(timeout.count() / 1000000000);
    tv.tv_usec = static_cast<int>(timeout.count() % 1000000000);

    fd_set write_fds;
    FD_ZERO(&write_fds);
    FD_SET(main_socket, &write_fds);

    int select_result = select(main_socket + 1, NULL, &write_fds, NULL, &tv);
    if (select_result == -1) return false;
    if (select_result == 0) return false;

    size_t total_written = 0;
    while (total_written < size) {
        ssize_t bytes_written = write(main_socket, static_cast<const char*>(buffer) + total_written, size - total_written);
        if (bytes_written <= 0) return false;
        total_written += bytes_written;
    }

    return true;
}

void TCPClient::Disconnect() {
    if (connected) {
        close(main_socket);
        connected = false;
    }
}

bool TCPClient::SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
    if (nonBlocking) {
        #ifdef _WIN32
        u_long mode = 1;
        if (ioctlsocket(sockFD, FIONBIO, &mode) != 0) return false;
        #else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        flags |= O_NONBLOCK;
        if (fcntl(sockFD, F_SETFL, flags) == -1) return false;
        #endif
    } else {
        #ifdef _WIN32
        u_long mode = 0;
        if (ioctlsocket(sockFD, FIONBIO, &mode) != 0) return false;
        #else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        flags &= ~O_NONBLOCK;
        if (fcntl(sockFD, F_SETFL, flags) == -1) return false;
        #endif
    }
    return true;
}

