    if (stats_thread_.joinable()) {
        stats_thread_.join();
    }
    close(sockfd_);
    unlink(stats_socket_path_.c_str());
}

void Stats::runSocket() {
    while (statsThreadRunning_) {
        struct sockaddr_un client_addr;
        socklen_t client_len = sizeof(client_addr);
        int client_sockfd = accept(sockfd_, (struct sockaddr*)&client_addr, &client_len);
        if (client_sockfd == -1) {
            continue;
        }
        ++thread_count_;
        std::thread t(&Stats::processMsg, this, client_sockfd);
        t.detach();
    }
}

void Stats::processMsg(int sockfd) {
    char buffer[1024];
    ssize_t bytes_read = read(sockfd, buffer, sizeof(buffer));
    if (bytes_read <= 0) {
        close(sockfd);
        return;
    }

    std::string request(buffer, bytes_read);
    std::istringstream iss(request);
    std::vector<std::string> tokens;
    std::string token;
    while (std::getline(iss, token, ' ')) {
        tokens.push_back(token);
    }

    if (tokens.empty()) {
        write(sockfd, "ERROR\n", 6);
        close(sockfd);
        return;
    }

    std::string command = tokens[0];
    if (command == "get") {
        if (tokens.size() < 2) {
            write(sockfd, "ERROR\n", 6);
            close(sockfd);
            return;
        }
        std::string key = tokens[1];
        std::lock_guard<std::mutex> lock(stats_mutex_);
        auto it = stats_.find(key);
        int value = (it != stats_.end()) ? it->second : 0;
        std::ostringstream oss;
        oss << value;
        write(sockfd, oss.str().c_str(), oss.str().length() + 1);
    } else if (command == "set") {
        if (tokens.size() < 3) {
            write(sockfd, "ERROR\n", 6);
            close(sockfd);
            return;
        }
        std::string key = tokens[1];
        int val = std::stoi(tokens[2]);
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        write(sockfd, "OK\n", 3);
    } else if (command == "increment") {
        if (tokens.size() < 3) {
            write(sockfd, "ERROR\n", 6);
            close(sockfd);
            return;
        }
        std::string key = tokens[1];
        int val = std::stoi(tokens[2]);
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] += val;
        write(sockfd, "OK\n", 3);
    } else if (command == "reset") {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        write(sockfd, "OK\n", 3);
    } else {
        write(sockfd, "ERROR\n", 6);
        close(sockfd);
    }
    close(sockfd);
}

int Stats::increment(const std::string& key, int val) {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    auto it = stats_.find(key);
    int current = (it != stats_.end()) ? it->second : 0;
    stats_[key] = current + val;
    return stats_[key];
}

int Stats::set(const std::string& key, int val) {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    stats_[key] = val;
    return stats_[key];
}

int Stats::reset() {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    stats_.clear();
    return 0;
}

std::unordered_map<std::string, int> Stats::getAll() {
    std::lock_guard<std::mutex> lock(stats_mutex_);
    return stats_;
}

bool Stats::startSocket() {
    return true;
}

bool Stats::init(const std::string& stats_socket_path) {
    if (is_initialized_) {
        return true;
    }
    instance_ = new Stats(stats_socket_path);
    is_initialized_ = true;
    return true;
}

bool Stats::isInit() {
    return is_initialized_;
}

bool Stats::isInitInternal() {
    return is_initialized_;
}

Stats& Stats::get(const std::string& stats_socket_path) {
    if (!is_initialized_) {
        if (!init(stats_socket_path)) {
            // Handle error
        }
    }
    return *instance_;
}

std::unique_ptr<Stats> Stats::get_for_unittest(const std::string& stats_socket_path) {
    return std::make_unique<Stats>(stats_socket_path);
}

