int UDPRecv::Init(int iPort) {
    m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
    if (m_iSockFD == -1) {
        return -1;
    }

    int flags = fcntl(m_iSockFD, F_GETFL, 0);
    if (flags == -1) {
        close(m_iSockFD);
        return -1;
    }
    if (fcntl(m_iSockFD, F_SETFL, flags | O_NONBLOCK) == -1) {
        close(m_iSockFD);
        return -1;
    }

    struct sockaddr_in addr;
    memset(&addr, 0, sizeof(addr));
    addr.sin_family = AF_INET;
    addr.sin_port = htons(iPort);
    addr.sin_addr.s_addr = INADDR_ANY;

    if (bind(m_iSockFD, (struct sockaddr*)&addr, sizeof(addr)) == -1) {
        close(m_iSockFD);
        return -1;
    }

    m_thread = std::thread(&UDPRecv::run, this);
    m_bIsStarted = true;
    return 0;
