#include <string>
#include <ctime>
#include <fstream>
#include <curl/curl.h>
#include <sys/utime.h>

class DownloadTask {
public:
    static void initialize() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name)
        : m_url(url), m_output(output), m_default(defaultFile), m_mtime(mtime), m_name(name), m_userAgent("Mozilla/5.0"), m_urlBackup(""), m_retries(0), m_error("") {
        m_outputTmp = "/tmp/" + m_name + "_tmp";
    }

    bool run() {
        m_retries = 0;
        m_error.clear();

        if (downloadFile(m_url)) {
            if (std::rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
                m_error = "Failed to rename temporary file to output";
                return false;
            }
            utimbuf tbuf;
            tbuf.actime = m_mtime;
            tbuf.modtime = m_mtime;
            utime(m_output.c_str(), &tbuf);
            return true;
        }

        if (m_urlBackup.empty()) {
            m_error = "No backup URL provided";
            return false;
        }

        m_retries++;
        if (downloadFile(m_urlBackup)) {
            if (std::rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
                m_error = "Failed to rename temporary file to output";
                return false;
            }
            utime(m_output.c_str(), &tbuf);
            return true;
        }

        m_error = "Failed to download from both URLs";
        return false;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

    static size_t writeData(void* contents, size_t size, size_t nmemb, void* userp) {
        DownloadTask* task = static_cast<DownloadTask*>(userp);
        size_t totalSize = size * nmemb;
        task->m_tmpFile.write(static_cast<char*>(contents), totalSize);
        return totalSize;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;
    std::ofstream m_tmpFile;

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to initialize curl";
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);

        m_tmpFile.open(m_outputTmp, std::ios::binary | std::ios::out | std::ios::trunc);
        if (!m_tmpFile) {
            m_error = "Failed to open temporary file";
            curl_easy_cleanup(curl);
            return false;
        }

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            m_error = "Curl error: " + std::string(curl_easy_strerror(res));
            m_tmpFile.close();
            curl_easy_cleanup(curl);
            return false;
        }

        long http_code = 0;
        curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
        if (http_code != 200) {
            m_error = "HTTP error code: " + std::to_string(http_code);
            m_tmpFile.close();
            curl_easy_cleanup(curl);
            return false;
        }

        m_tmpFile.close();
        curl_easy_cleanup(curl);
        return true;
    }
};

