class HttpWebResponse {
public:
    std::string content;
    int statusCode;
    std::map<std::string, std::string> headers;
};

class HttpWebRequest {
public:
    HttpWebRequest(std::string &uri);
    static size_t callback(void *buffer, size_t size, size_t nmemb, void *data);
    HttpWebResponse* GetFileResponse(const char *uri);
    HttpWebResponse* GetNetResponse(const char *uri);
    HttpWebResponse* GetResponse();
private:
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::istringstream is_;
};

HttpWebRequest::HttpWebRequest(std::string &uri) : uri_(uri), method_("GET"), header_(), is_() {}

size_t HttpWebRequest::callback(void *buffer, size_t size, size_t nmemb, void *data) {
    HttpWebResponse* response = static_cast<HttpWebResponse*>(data);
    response->content.append(static_cast<char*>(buffer), size * nmemb);
    return size * nmemb;
}

HttpWebResponse* HttpWebRequest::GetFileResponse(const char *uri) {
    std::string filePath = uri + 7;
    std::ifstream file(filePath);
    if (!file) {
        return nullptr;
    }
    std::string content((std::istreambuf_iterator<char>(file)), std::istreambuf_iterator<char>());
    HttpWebResponse* response = new HttpWebResponse();
    response->content = content;
    response->statusCode = 200;
    return response;
}

HttpWebResponse* HttpWebRequest::GetNetResponse(const char *uri) {
    CURL *curl = curl_easy_init();
    if (!curl) return nullptr;
    HttpWebResponse* response = new HttpWebResponse();
    curl_easy_setopt(curl, CURLOPT_URL, uri);
    curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
    curl_easy_setopt(curl, CURLOPT_WRITEDATA, response);
    if (method_ != "GET") {
        curl_easy_setopt(curl, CURLOPT_CUSTOMREQUEST, method_.c_str());
    }
    struct curl_slist *headers = NULL;
    for (const auto &pair : header_) {
        headers = curl_slist_append(headers, (pair.first + ": " + pair.second).c_str());
    }
    curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);
    curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
    curl_easy_setopt(curl, CURLOPT_TIMEOUT, 10L);
    CURLcode res = curl_easy_perform(curl);
    if (res != CURLE_OK) {
        curl_easy_cleanup(curl);
        delete response;
        return nullptr;
    }
    curl_easy_cleanup(curl);
    return response;
}

HttpWebResponse* HttpWebRequest::GetResponse() {
    if (uri_.find("file://") == 0) {
        return GetFileResponse(uri_.c_str());
    } else {
        return GetNetResponse(uri_.c_str());
    }
}

