        using ssl_stream_type = beast::websocket::stream<beast::ssl_stream<beast::tcp_stream>>;
        using buffer_type = beast::flat_buffer;

        explicit Impl(boost::asio::io_context&, ssl::context&, Device&, std::shared_ptr<Logging>);

        void run();
        void onResolve(beast::error_code, resolver_results);
        void onConnect(beast::error_code, endpoint_type);
        void onSSLhandshake(beast::error_code);
        void onWinsockHandshake(beast::error_code);
        void send(const std::string&, const std::string&);
        void onWrite(beast::error_code, std::size_t);
        void read();
        void onRead(beast::error_code, std::size_t);
        void onError(beast::error_code&, std::string);
        void enqueueWork(Work&);
        void close();

    private:
        boost::asio::io_context& ioc_;
        ssl::context& ctx_;
        Device device_settings_;
        std::shared_ptr<Logging> log_;
        resolver resolver_;
        std::optional<ssl_stream_type> ws_;
        std::optional<stream_type> ws_tcp_;
        buffer_type buffer_;
        std::string host_;
        int socket_status_;
    };
}

WebOsClient::Impl::Impl(boost::asio::io_context& ioc, ssl::context& ctx, Device& device, std::shared_ptr<Logging> log)
    : ioc_(ioc), ctx_(ctx), device_settings_(device), log_(log), host_(device.get_host()), socket_status_(0) {}

void WebOsClient::Impl::run() {
    log_->info("Starting WebSocket connection");
    resolver_.async_resolve(host_, std::to_string(device_settings_.get_port()), beast::bind_front_handler(&Impl::onResolve, this));
}

void WebOsClient::Impl::onResolve(beast::error_code ec, resolver_results results) {
    if (ec) {
        onError(ec, "Hostname resolution failed");
        return;
    }
    log_->info("Hostname resolved, connecting to endpoint");
    resolver_.async_connect(results, beast::bind_front_handler(&Impl::onConnect, this));
}

void WebOsClient::Impl::onConnect(beast::error_code ec, endpoint_type endpoint) {
    if (ec) {
        onError(ec, "Connection to endpoint failed");
        return;
    }
    log_->info("Connected to endpoint, establishing SSL/TLS");
    auto socket = std::make_unique<tcp::socket>(ioc_);
    socket->connect(endpoint);
    if (device_settings_.use_ssl()) {
        auto ssl_stream = std::make_unique<ssl::stream<tcp::socket>>(std::move(socket), ctx_);
        ssl_stream->async_handshake(ssl::stream_base::server, beast::bind_front_handler(&Impl::onSSLhandshake, this, std::move(ssl_stream)));
    } else {
        auto tcp_stream = std::make_unique<tcp::socket>(std::move(socket));
        auto ws = std::make_unique<stream_type>(std::move(tcp_stream));
        ws->async_handshake(endpoint, "/ws", beast::bind_front_handler(&Impl::onWinsockHandshake, this, std::move(ws)));
    }
}

void WebOsClient::Impl::onSSLhandshake(std::unique_ptr<ssl::stream<tcp::socket>> ssl_stream) {
    if (!ssl_stream) {
        onError(error_code(), "SSL stream not available");
        return;
    }
    auto ws = std::make_unique<stream_type>(std::move(ssl_stream));
    ws->async_handshake(endpoint(), "/ws", beast::bind_front_handler(&Impl::onWinsockHandshake, this, std::move(ws)));
}

void WebOsClient::Impl::onWinsockHandshake(std::unique_ptr<stream_type> ws) {
    if (!ws) {
        onError(error_code(), "WebSocket handshake failed");
        return;
    }
    if (device_settings_.use_ssl()) {
        ws_ = std::move(ws);
    } else {
        ws_tcp_ = std::move(ws);
    }
    socket_status_ = 2;
    log_->info("WebSocket handshake completed");
    read();
}

void WebOsClient::Impl::send(const std::string& target, const std::string& message) {
    if (socket_status_ != 2) {
        onError(error_code(), "Not connected to WebSocket server");
        return;
    }
    if (device_settings_.use_ssl()) {
        ws_->async_write(buffer_type(message), beast::bind_front_handler(&Impl::onWrite, this));
    } else {
        ws_tcp_->async_write(buffer_type(message), beast::bind_front_handler(&Impl::onWrite, this));
    }
}

void WebOsClient::Impl::onWrite(beast::error_code ec, std::size_t bytes_transferred) {
    if (ec) {
        onError(ec, "WebSocket write failed");
        return;
    }
    log_->info("Message sent successfully");
}

void WebOsClient::Impl::read() {
    if (socket_status_ != 2) {
        onError(error_code(), "Not connected to WebSocket server");
        return;
    }
    if (device_settings_.use_ssl()) {
        ws_->async_read(buffer_, beast::bind_front_handler(&Impl::onRead, this));
    } else {
        ws_tcp_->async_read(buffer_, beast::bind_front_handler(&Impl::onRead, this));
    }
}

void WebOsClient::Impl::onRead(beast::error_code ec, std::size_t bytes_transferred) {
    if (ec) {
        onError(ec, "WebSocket read failed");
        return;
    }
    log_->info("Received message of size: " + std::to_string(bytes_transferred));
    // Process received message and enqueue work
    enqueueWork(Work{});
    read();
}

void WebOsClient::Impl::onError(beast::error_code& ec, std::string message) {
    log_->error("WebSocket error: " + message + " - " + ec.message());
    socket_status_ = 0;
    // Attempt to reconnect or handle error
    if (ec == boost::asio::error::operation_aborted) {
        log_->info("Connection aborted, attempting to reconnect");
        run();
    }
}

void WebOsClient::Impl::enqueueWork(Work& work) {
    // Implementation for enqueuing work
}

void WebOsClient::Impl::close() {
    if (socket_status_ == 2) {
        if (device_settings_.use_ssl()) {
            ws_->close();
        } else {
            ws_tcp_->close();
        }
        socket_status_ = 0;
        log_->info("WebSocket connection closed");
    }
}

