package main

import (
	"fmt"
	"math/rand"
	"net/http"
	"time"

	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
)

func main() {
	e := echo.New()

	// Middleware
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	// Static files
	e.Static("/public", "public")

	// JSONP route
	e.GET("/jsonp", func(c echo.Context) error {
		callback := c.QueryParam("callback")
		if callback == "" {
			callback = "callback"
		}

		response := fmt.Sprintf("%s({\"message\":\"Hello, JSONP!\", \"timestamp\": %d, \"random\": %d})", callback, time.Now().Unix(), rand.Intn(100))
		return c.String(http.StatusOK, response)
	})

	// Start server
	e.Start(":1323")
}
